SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article`  (
  `id` bigint(20) NOT NULL COMMENT 'id',
  `cat_id` bigint(20) NULL DEFAULT NULL COMMENT '分类id',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `summary` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '摘要',
  `origin_content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL COMMENT '原始内容(markdown)',
  `convert_content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL COMMENT '转化后内容(html)',
  `view` int(10) NULL DEFAULT 0 COMMENT '浏览量',
  `article_status` tinyint(4) NOT NULL DEFAULT 0 COMMENT '状态，1：已发布；0：草稿箱；2：已删除',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `create_by` bigint(20) NULL DEFAULT NULL COMMENT '创建人',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '修改时间',
  `update_by` bigint(20) NULL DEFAULT NULL COMMENT '修改人',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_cat_id`(`cat_id`) USING BTREE COMMENT '分类id索引'
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '文章表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES (1649963237732524034, 1648580798401482753, '【事故】记一次意外把公司项目放到GitHub并被fork，如何使用DMCA下架政策保障隐私', '【事故】记一次意外把公司项目放到GitHub并被fork，如何使用DMCA下架政策保障隐私', '## 前言\n### 🍊缘由\n在一个月黑风高的夜晚，正准备休息的我突然接到之前**外包老总的亲切问候**。一顿输出才知道**三年前**为了搭建流程化部署，将公司的**测试代码**放到github上后**忘记删除**。现在被甲方的代码扫描机制扫到，并且检查到代码已经**被其他用户fork**，虽没有造成损失，但要求**清除github所有数据及被fork的代码**。以此为戒，特来跟大家分享一下处理流程，重点是**GitHub的DMCA 下架通知提交指南**。\n\n******\n### ⏲️本文阅读时长\n约10分钟\n\n******\n### 🎯主要目标\n1. 熟悉**GitHub的DMCA 下架通知提交指南**\n2. **代码涉密**被**别人fork**如何**在GitHub安全下架**\n3. 掌握**申诉流程**及**申诉时间点**把控\n\n******\n\n##  正文\n\n### 🍋1.判断政策分类\nDMCA 下架政策包含如下两种，根据自己情况如是选择。\n#### 1.GitHub 私人信息删除政策\n> “私人信息”是指符合以下条件的内容：应该保密，并且一旦公开，会对您或您的组织造成特定安全风险。\n\n**白话**：通俗来讲就是**没有版权**，涉及到**个人私密的文件**。如果想**删除库中单个文件审核比较轻松**，若是想删除整个库可能难度较大。所需材料如下：\n\n1. 可访问您组织的**服务器、网络或域的访问凭据，例如用户名与密码组合、访问令牌或其他敏感密钥**。\n2. 可代表您访问第三方的 **AWS 令牌**和其他类似**访问凭据**。 您必须能够证明该令牌确实属于您。\n3. 对组织构成特定安全风险的文档（例如**网络图或架构**）。\n4. 与您个人相关并对您构成安全风险的信息（如**社会保障号码**或其他**政府身份号码**）。\n\n#### 2.GitHub 商标政策\n> 在使用公司或企业名称、徽标或其他受商标保护的材料时，如果使用方式可能使他人对品牌或业务从属关系产生误解或感到困惑，则此类使用可能被视为违反商标政策。\n\n**白话**：通俗来讲就是侵犯到版权，被人举报侵权等，此审核所需材料较多，如下：\n\n1. 被举报帐户的用户名\n2. 贵公司名称\n3. 您的公司 GitHub 帐户（如果有）\n4. 公司网站\n5. 您商标中使用的词语、符号等。\n6. 商标注册号\n7. ......等等\n\n******\n### 🍏2.处理步骤\n\n#### GitHub 私人信息删除政策\n\n##### 1.访问GitHub 私人信息删除政策申请地址\n[https://support.github.com/contact/private-information](https://support.github.com/contact/private-information)\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d17df8751e084f2cbd9f12f819615c9b~tplv-k3u1fbpfcp-zoom-1.image)\n\n##### 2.解析每条问题如何选择及回复\n1. Company GitHub account (if applicable)\n 🏳️‍🌈翻译：公司 GitHub 帐户（如果适用）\n> 此处选择【Not Applicable/Other】(不适用/其他)\n\n2. Is the content you\'re reporting copyrighted work that you own?\n 🏳️‍🌈翻译：您报告的内容是您拥有的受版权保护的作品吗？\n> 此处选择【No】(不是)。此处也可选择YES，如果有版权保护可以直接通过【[GitHub 商标政策](https://support.github.com/contact/dmca-takedown)】申请即可\n\n3. Is your request related to a full repository or a specific file(s)?\n 🏳️‍🌈翻译：您的请求与完整存储库或特定文件相关吗？\n> 此处根据个人情况选择,若要删除整个库可能审核费劲，若选择删除单独文件则比较好通过。\n\n4. Please identify the content you are reporting. Select all that apply.\n 🏳️‍🌈翻译：请指明您举报的内容。 选择所有符合条件的。\n> 此处根据个人情况选择,其中包含如下5种选项，切记如果勾选，请把项目中涉及的敏感内容具体分布及多少行写清楚，有助于审核通过\n-  Access Credentials.【访问凭证】\n- AWS tokens and other similar access credentials.【AWS 令牌和其他类似的访问凭证】\n- Documentation (such as network diagrams or architecture) that poses a specific security risk for an organization. 【对组织构成特定安全风险的文档】\n- Information related to, and posing a security risk to, you as an individual (such as social security numbers or other government identification numbers).【与您个人相关并构成安全风险的信息（例如社会安全号码或其他政府身份证号码）】\n\n 🌰 举个本狗申请例子，本人勾选了Access Credentials，然后填写了具体内容\n```\nIt contains a lot of private information. \nFor example, the application-prod.yml configuration file under the path /src/main/resources/contains personal private database configuration information (address, account, password). For example, the sixth line of the application-prod.yml file contains the url of the database, and the seventh and eighth lines contain the database account password username: hrgc, password: xxx xxx. For another example, line 70 contains the key information of my bug component apikey: xxx.Also, in/src/main/java/com/xxx/user/api/XXXApi.java, line 12 and 13 contain the KEY and SECRET required by the user center, completely exposing private information. Others can break my server and database information through the information in the application-prod.yml file, which involves personal privacy and constitutes a security risk.\n\nAll contents of the following repository have been deleted.\nhttps://github.com/xxxx/xxxx\n\nHowever, the content can still be found in the link below. If personal information is involved, please delete it.\nhttps://github.com/xxx/xxxxx-trunk\nhttps://github.com/xxx/xxxxx-trunk\n\n```\n\n翻译如下\n```\n它包含很多私人信息。\n例如/src/main/resources/路径下的application-prod.yml配置文件包含个人私有数据库配置信息（地址、账号、密码）。 例如application-prod.yml文件第六行包含数据库的url，第七行和第八行包含数据库账号密码username：hrgc，password：xxx xxx。 再比如，第70行包含了我的bug组件apikey的关键信息：xxx.另外，在/src/main/java/com/xxx/user/api/XXXApi.java中，第12行和13行包含了需要的KEY和SECRET 通过用户中心，彻底暴露隐私信息。 其他人可以通过application-prod.yml文件中的信息破解我的服务器和数据库信息，涉及个人隐私，存在安全隐患。\n\n以下存储库的所有内容已被删除。\nhttps://github.com/xxxx/xxxx\n\n但是，仍然可以在下面的链接中找到内容。 如涉及个人信息，请删除。\nhttps://github.com/xxx/xxxxx-trunk\nhttps://github.com/xxx/xxxxx-trunk\n```\n \n 5. Have you attempted to contact the GitHub user(s) who uploaded the sensitive data?\n🏳️‍🌈翻译：您是否尝试联系上传敏感数据的 GitHub 用户？\n> 此处根据个人情况选择,询问是否自己尝试联系过fork自己代码的GitHub用户，无关紧要，本狗选择【No】\n\n 6. Is the request particularly time-sensitive?\n🏳️‍🌈翻译：该请求是否对时间特别敏感？\n> 询问是否着急，对时间要求紧。本狗选择【No】如果写Yes还需要描述：报告的数据暴露了多长时间？ 您是否可以分享任何您认为会使此请求特别紧迫的信息？\n\n7. Are you a third party acting as an agent for an organization facing a security risk?\n🏳️‍🌈翻译：您是作为面临安全风险的组织的代理的第三方吗？\n> 本狗选择【No】如果写Yes还需要描述：请附上一份声明，说明您有合法权利代表该组织行事。\n\n8. How long has the reported content been publicly available?\n🏳️‍🌈翻译：举报的内容公开多久了？\n> 本狗选择【24+ months】因为是三年前项目了，公开了3年之久了。此处可根据自己实际情况选择即可。\n\n9. I confirm that I have read the GitHub Private Information Removal Policy\n🏳️‍🌈翻译：我确认我已阅读 GitHub 私人信息删除政策\n> 此处勾选同意即可\n\n##### 3.申请总览图\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9daa5cd5c5e249f4ae876bccc2697df8~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/0b927fffdf9d4b35a6b16b4962149d9a~tplv-k3u1fbpfcp-zoom-1.image)\n##### 4.点击【发送请求】\n>如果成功GitHub会回执一分邮件，告知你已经接受到\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4040599434494036a9b715e8b46e307b~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n\n#### GitHub 商标政策\n\n##### 1.访问GitHub 商标政策申请地址\n[https://support.github.com/contact/dmca-takedown](https://support.github.com/contact/dmca-takedown)\n\n##### 2.解析每条问题如何选择及回复\n1. Are you the copyright holder or authorized to act on the copyright owner\'s behalf?\n 🏳️‍🌈翻译：您是版权所有者或授权代表版权所有者行事吗？\n> 此处选择【Yes, I am the copyright holder.】是的，我是版权所有者。\n\n2. Are you submitting a revised DMCA notice after GitHub Trust & Safety requested you make changes to your original notice?\n 🏳️‍🌈翻译：在 GitHub Trust & Safety 要求您更改原始通知后，您是否提交了修订后的 DMCA 通知？\n> 此处选择【No】\n\n3. Does your claim involve content on GitHub or npm.js?\n 🏳️‍🌈翻译：您的声明是否涉及 GitHub 或 npm.js 上的内容？\n> 此处选择【GitHub】，如果有其他情况可根据实际情况选择\n\n4. Please describe the nature of your copyright ownership or authorization to act on the owner\'s behalf.\n 🏳️‍🌈翻译：请描述您的版权所有权或授权代表所有者行事的性质。\n> 此处进行填入自己版权所属权的相关信息，如下包含示例及翻译\n\n> We have software(https://github.com/xxxx/xxxx) that was developed in-house and one of our developers uploaded it to GitHub and made it public. We notified this former employee who promptly deleted the content, but anothers has a copy. We made contact for deletion, but he did not respond to our messages.It contains a lot of private information. For example, the application-prod.yml configuration file under the path/src/main/resources/contains personal private database configuration information (address, account, password). For example, the sixth line of the application-prod.yml file contains the url of the database, and the seventh and eighth lines contain the database account password username: hrgc, password: xxx 123. For another example, line 70 contains the key information of my bug component apikey: xxxx.Also, in/src/main/java/com/xxxx/user/api/ProductsApi.java, line 12 and 13 contain the KEY and SECRET required by the user center, completely exposing private information. Others can break my server and database information through the information in the application-prod.yml file, which involves company privacy and constitutes a security risk.\n\n> 我们有内部开发的软件（https://github.com/xxxx/xxxx），我们的一位开发人员将其上传到 GitHub 并公开。 我们通知了这位前雇员，他立即删除了内容，但其他人有一份副本。 我们联系删除，他没有回复我们的消息，里面有很多隐私信息。 比如路径/src/main/resources/下的application-prod.yml配置文件包含个人私有数据库配置信息（地址、账号、密码）。 例如application-prod.yml文件第六行包含数据库的url，第七行和第八行包含数据库账号密码用户名：hrgc，密码：xxx 123。再比如第70行包含key 我的bug组件apikey的信息：xxxx.另外，在/src/main/java/com/xxxx/user/api/ProductsApi.java中，第12行和第13行包含了用户中心需要的KEY和SECRET，完全暴露了隐私信息 . 其他人可以通过application-prod.yml文件中的信息破解我的服务器和数据库信息，涉及公司隐私，存在安全隐患。\n\n5. Please provide a detailed description of the original copyrighted work that has allegedly been infringed. If possible, include a URL to where it is posted online.\n 🏳️‍🌈翻译：请提供涉嫌侵权的原始版权作品的详细描述。 如果可能，包括在线发布的 URL。\n> 此处进行填入自己代码仓库地址及别人fork之后的地址清单，如下包含示例及翻译\n\n> All contents of the following repository have been deleted.\nhttps://github.com/xxxx/xxxx-trunk\nHowever, the content can still be found in the link below. If personal information is involved, please delete it.\nhttps://github.com/xxxx/xxxx-trunk\nhttps://github.com/xxxx/xxxx-trunk\n\n> 下列储存库的所有内容已被删除。\nhttps://github.com/xxxx/xxxx\n不过，内容还是可以在下面的链接中找到。如涉及个人信息，请及时删除。\nhttps://github.com/xxxx/xxxx\nhttps://github.com/xxxx/xxxx\n\n6. What files should be taken down? Please provide URLs for each file, or if the entire repository, the repository’s URL.\n 🏳️‍🌈翻译：应该删除哪些文件？ 请提供每个文件的 URL，或者如果是整个存储库，请提供存储库的 URL。\n> 此处填入想要删除的分支或者被fork的仓库的路径，若想删除单独文件，可提供单独文件的URL\n\n> All repository content.\nhttps://github.com/xxxx/xxxx\nhttps://github.com/xxxx/xxxx\n\n>所有存储库内容。\nhttps://github.com/xxxx/xxxx\nhttps://github.com/xxxx/xxxx\n\n7. Do you claim to have any technological measures in place to control access to your copyrighted content? Please see our Complaints about Anti-Circumvention Technology if you are unsure.\n 🏳️‍🌈翻译：您是否声称已采取任何技术措施来控制对您受版权保护的内容的访问？ 如果您不确定，请参阅我们关于反规避技术的投诉。\n> 此处填入【No】\n\n8. Have you searched for any forks of the allegedly infringing files or repositories? Each fork is a distinct repository and must be identified separately if you believe it is infringing and wish to have it taken down.\n 🏳️‍🌈翻译：您是否搜索过涉嫌侵权的文件或存储库的任何分支？ 每个分支都是一个不同的存储库，如果您认为它侵权并希望将其删除，则必须单独标识。\n> 此处填入你搜索过想要删除的其他分支及fork侵权的仓库地址\n\n>No forks have been identified other than the one below.\nhttps://github.com/xxxx/xxxx-trunk\nhttps://github.com/xxxx/xxxx-trunk\n\n> 除下面的fork外，没有发现其他fork。\n> https://github.com/xxxx/xxxx\n> https://github.com/xxxx/xxxxx\n\n9. Is the work licensed under an open source license?\n 🏳️‍🌈翻译：该作品是否在开源许可下获得许可？\n> 此处填入【No】\n\n10. What would be the best solution for the alleged infringement?\n 🏳️‍🌈翻译：对于涉嫌侵权的最佳解决方案是什么？\n> 此处选择【Reported content must be removed】必须删除举报的内容\n\n11. Do you have the alleged infringer’s contact information? If so, please provide it.\n 🏳️‍🌈翻译：您有涉嫌侵权人的联系方式吗？ 如果有，请提供。\n> 此处填入【No】\n\n12. 勾选确认如下4条信息\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e6ef321173fc4cfda3350805734fe9b8~tplv-k3u1fbpfcp-zoom-1.image)\n>我真诚地相信，在侵权网页上使用上述受版权保护的材料未经版权所有者或其代理人或法律授权。\n我发誓，根据伪证处罚，本通知中的信息准确无误，并且我是涉嫌侵权的专有权的版权所有者，或被授权代表所有者行事。\n我已经考虑了合理使用。\n我已阅读并理解 GitHub 的提交 DMCA 删除通知指南。\n\n13. So that we can get back to you, please provide either your telephone number or physical address.\n 🏳️‍🌈翻译：为了便于我们回复您，请提供您的电话号码或实际地址。\n> 此处填入自己的邮箱或者电话号码\n\n14. Please type your full legal name below to sign this request.\n 🏳️‍🌈翻译：请在下方输入您的法定全名以签署此请求。\n> 此处填入自己的真实名字\n\n##### 3.申请总览图\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/eb633b814cb24eb8b575c5cd17365765~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/66500e49b1274a0ca061d23d02a03e02~tplv-k3u1fbpfcp-zoom-1.image)\n##### 4.点击【发送请求】\n>如果成功GitHub会回执一分邮件，告知你已经接受到\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/431540b18a004e1b8bde138ae8d213ab~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n###  🍉3.发送时间\n因GitHub是漂亮国所属，**人工审核按照他们上班时间处理**，与咱们时间相差十个小时左右，所以建议**晚上八点后发送**，如运气好则会**24点前**收到回复，再根据回复内容修改不符合要求内容。若不想熬夜则第二天再次查看即可，切勿着急！！！着急也没用，运气好一般审核的时间差在4个小时左右。\n\n\n## 😊总结\n> 程序员删库跑路单干，结局大概是要吃牢饭！\n\n通过此次的意外事故，学习到项目隐私及版权相关知识，并熟悉了**DMCA下架政策保障隐私**。希望大家以此为戒，永远都用不到这些。\n\n\n![里面有我喜欢的人你能找到吗？](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/aa2399e1ff8d45d98cd612e2840b9d8d~tplv-k3u1fbpfcp-zoom-1.image)\n\n\n| JavaDog| 狗屋地址 |\n| :----:| :----: | \n| 个人博客 | [https://blog.javadog.net](https://blog.javadog.net) | \n| 公众号 | [https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw](https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw) | \n| CSDN  | [https://blog.csdn.net/baidu_25986059](https://blog.csdn.net/baidu_25986059) | \n| 掘金 | [https://juejin.cn/user/2172290706716775](https://juejin.cn/user/2172290706716775)| \n| 知乎 | [https://www.zhihu.com/people/JavaDog](https://www.zhihu.com/people/JavaDog) | \n| 简书| [https://www.jianshu.com/u/1ff9c6bdb916](https://www.jianshu.com/u/1ff9c6bdb916) | \n| gitee|[https://gitee.com/javadog-net](https://gitee.com/javadog-net)  | \n| GitHub|[https://github.com/javadog-net](https://github.com/javadog-net)| \n', '<h2><a id=\"_0\"></a>前言</h2>\n<h3><a id=\"_1\"></a>🍊缘由</h3>\n<p>在一个月黑风高的夜晚，正准备休息的我突然接到之前<strong>外包老总的亲切问候</strong>。一顿输出才知道<strong>三年前</strong>为了搭建流程化部署，将公司的<strong>测试代码</strong>放到github上后<strong>忘记删除</strong>。现在被甲方的代码扫描机制扫到，并且检查到代码已经<strong>被其他用户fork</strong>，虽没有造成损失，但要求<strong>清除github所有数据及被fork的代码</strong>。以此为戒，特来跟大家分享一下处理流程，重点是<strong>GitHub的DMCA 下架通知提交指南</strong>。</p>\n<hr />\n<h3><a id=\"_5\"></a>⏲️本文阅读时长</h3>\n<p>约10分钟</p>\n<hr />\n<h3><a id=\"_9\"></a>🎯主要目标</h3>\n<ol>\n<li>熟悉<strong>GitHub的DMCA 下架通知提交指南</strong></li>\n<li><strong>代码涉密</strong>被<strong>别人fork</strong>如何<strong>在GitHub安全下架</strong></li>\n<li>掌握<strong>申诉流程</strong>及<strong>申诉时间点</strong>把控</li>\n</ol>\n<hr />\n<h2><a id=\"_16\"></a>正文</h2>\n<h3><a id=\"1_18\"></a>🍋1.判断政策分类</h3>\n<p>DMCA 下架政策包含如下两种，根据自己情况如是选择。</p>\n<h4><a id=\"1GitHub__20\"></a>1.GitHub 私人信息删除政策</h4>\n<blockquote>\n<p>“私人信息”是指符合以下条件的内容：应该保密，并且一旦公开，会对您或您的组织造成特定安全风险。</p>\n</blockquote>\n<p><strong>白话</strong>：通俗来讲就是<strong>没有版权</strong>，涉及到<strong>个人私密的文件</strong>。如果想<strong>删除库中单个文件审核比较轻松</strong>，若是想删除整个库可能难度较大。所需材料如下：</p>\n<ol>\n<li>可访问您组织的<strong>服务器、网络或域的访问凭据，例如用户名与密码组合、访问令牌或其他敏感密钥</strong>。</li>\n<li>可代表您访问第三方的 <strong>AWS 令牌</strong>和其他类似<strong>访问凭据</strong>。 您必须能够证明该令牌确实属于您。</li>\n<li>对组织构成特定安全风险的文档（例如<strong>网络图或架构</strong>）。</li>\n<li>与您个人相关并对您构成安全风险的信息（如<strong>社会保障号码</strong>或其他<strong>政府身份号码</strong>）。</li>\n</ol>\n<h4><a id=\"2GitHub__30\"></a>2.GitHub 商标政策</h4>\n<blockquote>\n<p>在使用公司或企业名称、徽标或其他受商标保护的材料时，如果使用方式可能使他人对品牌或业务从属关系产生误解或感到困惑，则此类使用可能被视为违反商标政策。</p>\n</blockquote>\n<p><strong>白话</strong>：通俗来讲就是侵犯到版权，被人举报侵权等，此审核所需材料较多，如下：</p>\n<ol>\n<li>被举报帐户的用户名</li>\n<li>贵公司名称</li>\n<li>您的公司 GitHub 帐户（如果有）</li>\n<li>公司网站</li>\n<li>您商标中使用的词语、符号等。</li>\n<li>商标注册号</li>\n<li>…等等</li>\n</ol>\n<hr />\n<h3><a id=\"2_44\"></a>🍏2.处理步骤</h3>\n<h4><a id=\"GitHub__46\"></a>GitHub 私人信息删除政策</h4>\n<h5><a id=\"1GitHub__48\"></a>1.访问GitHub 私人信息删除政策申请地址</h5>\n<p><a href=\"https://support.github.com/contact/private-information\" target=\"_blank\">https://support.github.com/contact/private-information</a></p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d17df8751e084f2cbd9f12f819615c9b~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h5><a id=\"2_53\"></a>2.解析每条问题如何选择及回复</h5>\n<ol>\n<li>Company GitHub account (if applicable)<br />\n🏳️‍🌈翻译：公司 GitHub 帐户（如果适用）</li>\n</ol>\n<blockquote>\n<p>此处选择【Not Applicable/Other】(不适用/其他)</p>\n</blockquote>\n<ol start=\"2\">\n<li>Is the content you’re reporting copyrighted work that you own?<br />\n🏳️‍🌈翻译：您报告的内容是您拥有的受版权保护的作品吗？</li>\n</ol>\n<blockquote>\n<p>此处选择【No】(不是)。此处也可选择YES，如果有版权保护可以直接通过【<a href=\"https://support.github.com/contact/dmca-takedown\" target=\"_blank\">GitHub 商标政策</a>】申请即可</p>\n</blockquote>\n<ol start=\"3\">\n<li>Is your request related to a full repository or a specific file(s)?<br />\n🏳️‍🌈翻译：您的请求与完整存储库或特定文件相关吗？</li>\n</ol>\n<blockquote>\n<p>此处根据个人情况选择,若要删除整个库可能审核费劲，若选择删除单独文件则比较好通过。</p>\n</blockquote>\n<ol start=\"4\">\n<li>Please identify the content you are reporting. Select all that apply.<br />\n🏳️‍🌈翻译：请指明您举报的内容。 选择所有符合条件的。</li>\n</ol>\n<blockquote>\n<p>此处根据个人情况选择,其中包含如下5种选项，切记如果勾选，请把项目中涉及的敏感内容具体分布及多少行写清楚，有助于审核通过</p>\n</blockquote>\n<ul>\n<li>Access Credentials.【访问凭证】</li>\n<li>AWS tokens and other similar access credentials.【AWS 令牌和其他类似的访问凭证】</li>\n<li>Documentation (such as network diagrams or architecture) that poses a specific security risk for an organization. 【对组织构成特定安全风险的文档】</li>\n<li>Information related to, and posing a security risk to, you as an individual (such as social security numbers or other government identification numbers).【与您个人相关并构成安全风险的信息（例如社会安全号码或其他政府身份证号码）】</li>\n</ul>\n<p>🌰 举个本狗申请例子，本人勾选了Access Credentials，然后填写了具体内容</p>\n<pre><code class=\"lang-\">It contains a lot of private information. \nFor example, the application-prod.yml configuration file under the path /src/main/resources/contains personal private database configuration information (address, account, password). For example, the sixth line of the application-prod.yml file contains the url of the database, and the seventh and eighth lines contain the database account password username: hrgc, password: xxx xxx. For another example, line 70 contains the key information of my bug component apikey: xxx.Also, in/src/main/java/com/xxx/user/api/XXXApi.java, line 12 and 13 contain the KEY and SECRET required by the user center, completely exposing private information. Others can break my server and database information through the information in the application-prod.yml file, which involves personal privacy and constitutes a security risk.\n\nAll contents of the following repository have been deleted.\nhttps://github.com/xxxx/xxxx\n\nHowever, the content can still be found in the link below. If personal information is involved, please delete it.\nhttps://github.com/xxx/xxxxx-trunk\nhttps://github.com/xxx/xxxxx-trunk\n\n</code></pre>\n<p>翻译如下</p>\n<pre><code class=\"lang-\">它包含很多私人信息。\n例如/src/main/resources/路径下的application-prod.yml配置文件包含个人私有数据库配置信息（地址、账号、密码）。 例如application-prod.yml文件第六行包含数据库的url，第七行和第八行包含数据库账号密码username：hrgc，password：xxx xxx。 再比如，第70行包含了我的bug组件apikey的关键信息：xxx.另外，在/src/main/java/com/xxx/user/api/XXXApi.java中，第12行和13行包含了需要的KEY和SECRET 通过用户中心，彻底暴露隐私信息。 其他人可以通过application-prod.yml文件中的信息破解我的服务器和数据库信息，涉及个人隐私，存在安全隐患。\n\n以下存储库的所有内容已被删除。\nhttps://github.com/xxxx/xxxx\n\n但是，仍然可以在下面的链接中找到内容。 如涉及个人信息，请删除。\nhttps://github.com/xxx/xxxxx-trunk\nhttps://github.com/xxx/xxxxx-trunk\n</code></pre>\n<ol start=\"5\">\n<li>Have you attempted to contact the GitHub user(s) who uploaded the sensitive data?<br />\n🏳️‍🌈翻译：您是否尝试联系上传敏感数据的 GitHub 用户？</li>\n</ol>\n<blockquote>\n<p>此处根据个人情况选择,询问是否自己尝试联系过fork自己代码的GitHub用户，无关紧要，本狗选择【No】</p>\n</blockquote>\n<ol start=\"6\">\n<li>Is the request particularly time-sensitive?<br />\n🏳️‍🌈翻译：该请求是否对时间特别敏感？</li>\n</ol>\n<blockquote>\n<p>询问是否着急，对时间要求紧。本狗选择【No】如果写Yes还需要描述：报告的数据暴露了多长时间？ 您是否可以分享任何您认为会使此请求特别紧迫的信息？</p>\n</blockquote>\n<ol start=\"7\">\n<li>Are you a third party acting as an agent for an organization facing a security risk?<br />\n🏳️‍🌈翻译：您是作为面临安全风险的组织的代理的第三方吗？</li>\n</ol>\n<blockquote>\n<p>本狗选择【No】如果写Yes还需要描述：请附上一份声明，说明您有合法权利代表该组织行事。</p>\n</blockquote>\n<ol start=\"8\">\n<li>How long has the reported content been publicly available?<br />\n🏳️‍🌈翻译：举报的内容公开多久了？</li>\n</ol>\n<blockquote>\n<p>本狗选择【24+ months】因为是三年前项目了，公开了3年之久了。此处可根据自己实际情况选择即可。</p>\n</blockquote>\n<ol start=\"9\">\n<li>I confirm that I have read the GitHub Private Information Removal Policy<br />\n🏳️‍🌈翻译：我确认我已阅读 GitHub 私人信息删除政策</li>\n</ol>\n<blockquote>\n<p>此处勾选同意即可</p>\n</blockquote>\n<h5><a id=\"3_121\"></a>3.申请总览图</h5>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9daa5cd5c5e249f4ae876bccc2697df8~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/0b927fffdf9d4b35a6b16b4962149d9a~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h5><a id=\"4_124\"></a>4.点击【发送请求】</h5>\n<blockquote>\n<p>如果成功GitHub会回执一分邮件，告知你已经接受到</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4040599434494036a9b715e8b46e307b~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"GitHub__131\"></a>GitHub 商标政策</h4>\n<h5><a id=\"1GitHub__133\"></a>1.访问GitHub 商标政策申请地址</h5>\n<p><a href=\"https://support.github.com/contact/dmca-takedown\" target=\"_blank\">https://support.github.com/contact/dmca-takedown</a></p>\n<h5><a id=\"2_136\"></a>2.解析每条问题如何选择及回复</h5>\n<ol>\n<li>Are you the copyright holder or authorized to act on the copyright owner’s behalf?<br />\n🏳️‍🌈翻译：您是版权所有者或授权代表版权所有者行事吗？</li>\n</ol>\n<blockquote>\n<p>此处选择【Yes, I am the copyright holder.】是的，我是版权所有者。</p>\n</blockquote>\n<ol start=\"2\">\n<li>Are you submitting a revised DMCA notice after GitHub Trust &amp; Safety requested you make changes to your original notice?<br />\n🏳️‍🌈翻译：在 GitHub Trust &amp; Safety 要求您更改原始通知后，您是否提交了修订后的 DMCA 通知？</li>\n</ol>\n<blockquote>\n<p>此处选择【No】</p>\n</blockquote>\n<ol start=\"3\">\n<li>Does your claim involve content on GitHub or npm.js?<br />\n🏳️‍🌈翻译：您的声明是否涉及 GitHub 或 npm.js 上的内容？</li>\n</ol>\n<blockquote>\n<p>此处选择【GitHub】，如果有其他情况可根据实际情况选择</p>\n</blockquote>\n<ol start=\"4\">\n<li>Please describe the nature of your copyright ownership or authorization to act on the owner’s behalf.<br />\n🏳️‍🌈翻译：请描述您的版权所有权或授权代表所有者行事的性质。</li>\n</ol>\n<blockquote>\n<p>此处进行填入自己版权所属权的相关信息，如下包含示例及翻译</p>\n</blockquote>\n<blockquote>\n<p>We have software(https://github.com/xxxx/xxxx) that was developed in-house and one of our developers uploaded it to GitHub and made it public. We notified this former employee who promptly deleted the content, but anothers has a copy. We made contact for deletion, but he did not respond to our messages.It contains a lot of private information. For example, the application-prod.yml configuration file under the path/src/main/resources/contains personal private database configuration information (address, account, password). For example, the sixth line of the application-prod.yml file contains the url of the database, and the seventh and eighth lines contain the database account password username: hrgc, password: xxx 123. For another example, line 70 contains the key information of my bug component apikey: xxxx.Also, in/src/main/java/com/xxxx/user/api/ProductsApi.java, line 12 and 13 contain the KEY and SECRET required by the user center, completely exposing private information. Others can break my server and database information through the information in the application-prod.yml file, which involves company privacy and constitutes a security risk.</p>\n</blockquote>\n<blockquote>\n<p>我们有内部开发的软件（https://github.com/xxxx/xxxx），我们的一位开发人员将其上传到 GitHub 并公开。 我们通知了这位前雇员，他立即删除了内容，但其他人有一份副本。 我们联系删除，他没有回复我们的消息，里面有很多隐私信息。 比如路径/src/main/resources/下的application-prod.yml配置文件包含个人私有数据库配置信息（地址、账号、密码）。 例如application-prod.yml文件第六行包含数据库的url，第七行和第八行包含数据库账号密码用户名：hrgc，密码：xxx 123。再比如第70行包含key 我的bug组件apikey的信息：xxxx.另外，在/src/main/java/com/xxxx/user/api/ProductsApi.java中，第12行和第13行包含了用户中心需要的KEY和SECRET，完全暴露了隐私信息 . 其他人可以通过application-prod.yml文件中的信息破解我的服务器和数据库信息，涉及公司隐私，存在安全隐患。</p>\n</blockquote>\n<ol start=\"5\">\n<li>Please provide a detailed description of the original copyrighted work that has allegedly been infringed. If possible, include a URL to where it is posted online.<br />\n🏳️‍🌈翻译：请提供涉嫌侵权的原始版权作品的详细描述。 如果可能，包括在线发布的 URL。</li>\n</ol>\n<blockquote>\n<p>此处进行填入自己代码仓库地址及别人fork之后的地址清单，如下包含示例及翻译</p>\n</blockquote>\n<blockquote>\n<p>All contents of the following repository have been deleted.<br />\nhttps://github.com/xxxx/xxxx-trunk<br />\nHowever, the content can still be found in the link below. If personal information is involved, please delete it.<br />\nhttps://github.com/xxxx/xxxx-trunk<br />\nhttps://github.com/xxxx/xxxx-trunk</p>\n</blockquote>\n<blockquote>\n<p>下列储存库的所有内容已被删除。<br />\nhttps://github.com/xxxx/xxxx<br />\n不过，内容还是可以在下面的链接中找到。如涉及个人信息，请及时删除。<br />\nhttps://github.com/xxxx/xxxx<br />\nhttps://github.com/xxxx/xxxx</p>\n</blockquote>\n<ol start=\"6\">\n<li>What files should be taken down? Please provide URLs for each file, or if the entire repository, the repository’s URL.<br />\n🏳️‍🌈翻译：应该删除哪些文件？ 请提供每个文件的 URL，或者如果是整个存储库，请提供存储库的 URL。</li>\n</ol>\n<blockquote>\n<p>此处填入想要删除的分支或者被fork的仓库的路径，若想删除单独文件，可提供单独文件的URL</p>\n</blockquote>\n<blockquote>\n<p>All repository content.<br />\nhttps://github.com/xxxx/xxxx<br />\nhttps://github.com/xxxx/xxxx</p>\n</blockquote>\n<blockquote>\n<p>所有存储库内容。<br />\nhttps://github.com/xxxx/xxxx<br />\nhttps://github.com/xxxx/xxxx</p>\n</blockquote>\n<ol start=\"7\">\n<li>Do you claim to have any technological measures in place to control access to your copyrighted content? Please see our Complaints about Anti-Circumvention Technology if you are unsure.<br />\n🏳️‍🌈翻译：您是否声称已采取任何技术措施来控制对您受版权保护的内容的访问？ 如果您不确定，请参阅我们关于反规避技术的投诉。</li>\n</ol>\n<blockquote>\n<p>此处填入【No】</p>\n</blockquote>\n<ol start=\"8\">\n<li>Have you searched for any forks of the allegedly infringing files or repositories? Each fork is a distinct repository and must be identified separately if you believe it is infringing and wish to have it taken down.<br />\n🏳️‍🌈翻译：您是否搜索过涉嫌侵权的文件或存储库的任何分支？ 每个分支都是一个不同的存储库，如果您认为它侵权并希望将其删除，则必须单独标识。</li>\n</ol>\n<blockquote>\n<p>此处填入你搜索过想要删除的其他分支及fork侵权的仓库地址</p>\n</blockquote>\n<blockquote>\n<p>No forks have been identified other than the one below.<br />\nhttps://github.com/xxxx/xxxx-trunk<br />\nhttps://github.com/xxxx/xxxx-trunk</p>\n</blockquote>\n<blockquote>\n<p>除下面的fork外，没有发现其他fork。<br />\nhttps://github.com/xxxx/xxxx<br />\nhttps://github.com/xxxx/xxxxx</p>\n</blockquote>\n<ol start=\"9\">\n<li>Is the work licensed under an open source license?<br />\n🏳️‍🌈翻译：该作品是否在开源许可下获得许可？</li>\n</ol>\n<blockquote>\n<p>此处填入【No】</p>\n</blockquote>\n<ol start=\"10\">\n<li>What would be the best solution for the alleged infringement?<br />\n🏳️‍🌈翻译：对于涉嫌侵权的最佳解决方案是什么？</li>\n</ol>\n<blockquote>\n<p>此处选择【Reported content must be removed】必须删除举报的内容</p>\n</blockquote>\n<ol start=\"11\">\n<li>Do you have the alleged infringer’s contact information? If so, please provide it.<br />\n🏳️‍🌈翻译：您有涉嫌侵权人的联系方式吗？ 如果有，请提供。</li>\n</ol>\n<blockquote>\n<p>此处填入【No】</p>\n</blockquote>\n<ol start=\"12\">\n<li>勾选确认如下4条信息<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e6ef321173fc4cfda3350805734fe9b8~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></li>\n</ol>\n<blockquote>\n<p>我真诚地相信，在侵权网页上使用上述受版权保护的材料未经版权所有者或其代理人或法律授权。<br />\n我发誓，根据伪证处罚，本通知中的信息准确无误，并且我是涉嫌侵权的专有权的版权所有者，或被授权代表所有者行事。<br />\n我已经考虑了合理使用。<br />\n我已阅读并理解 GitHub 的提交 DMCA 删除通知指南。</p>\n</blockquote>\n<ol start=\"13\">\n<li>So that we can get back to you, please provide either your telephone number or physical address.<br />\n🏳️‍🌈翻译：为了便于我们回复您，请提供您的电话号码或实际地址。</li>\n</ol>\n<blockquote>\n<p>此处填入自己的邮箱或者电话号码</p>\n</blockquote>\n<ol start=\"14\">\n<li>Please type your full legal name below to sign this request.<br />\n🏳️‍🌈翻译：请在下方输入您的法定全名以签署此请求。</li>\n</ol>\n<blockquote>\n<p>此处填入自己的真实名字</p>\n</blockquote>\n<h5><a id=\"3_228\"></a>3.申请总览图</h5>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/eb633b814cb24eb8b575c5cd17365765~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/66500e49b1274a0ca061d23d02a03e02~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h5><a id=\"4_231\"></a>4.点击【发送请求】</h5>\n<blockquote>\n<p>如果成功GitHub会回执一分邮件，告知你已经接受到</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/431540b18a004e1b8bde138ae8d213ab~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h3><a id=\"3_237\"></a>🍉3.发送时间</h3>\n<p>因GitHub是漂亮国所属，<strong>人工审核按照他们上班时间处理</strong>，与咱们时间相差十个小时左右，所以建议<strong>晚上八点后发送</strong>，如运气好则会<strong>24点前</strong>收到回复，再根据回复内容修改不符合要求内容。若不想熬夜则第二天再次查看即可，切勿着急！！！着急也没用，运气好一般审核的时间差在4个小时左右。</p>\n<h2><a id=\"_241\"></a>😊总结</h2>\n<blockquote>\n<p>程序员删库跑路单干，结局大概是要吃牢饭！</p>\n</blockquote>\n<p>通过此次的意外事故，学习到项目隐私及版权相关知识，并熟悉了<strong>DMCA下架政策保障隐私</strong>。希望大家以此为戒，永远都用不到这些。</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/aa2399e1ff8d45d98cd612e2840b9d8d~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"里面有我喜欢的人你能找到吗？\" /></p>\n<table>\n<thead>\n<tr>\n<th style=\"text-align:center\">JavaDog</th>\n<th style=\"text-align:center\">狗屋地址</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td style=\"text-align:center\">个人博客</td>\n<td style=\"text-align:center\"><a href=\"https://blog.javadog.net\" target=\"_blank\">https://blog.javadog.net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">公众号</td>\n<td style=\"text-align:center\"><a href=\"https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw\" target=\"_blank\">https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">CSDN</td>\n<td style=\"text-align:center\"><a href=\"https://blog.csdn.net/baidu_25986059\" target=\"_blank\">https://blog.csdn.net/baidu_25986059</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">掘金</td>\n<td style=\"text-align:center\"><a href=\"https://juejin.cn/user/2172290706716775\" target=\"_blank\">https://juejin.cn/user/2172290706716775</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">知乎</td>\n<td style=\"text-align:center\"><a href=\"https://www.zhihu.com/people/JavaDog\" target=\"_blank\">https://www.zhihu.com/people/JavaDog</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">简书</td>\n<td style=\"text-align:center\"><a href=\"https://www.jianshu.com/u/1ff9c6bdb916\" target=\"_blank\">https://www.jianshu.com/u/1ff9c6bdb916</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">gitee</td>\n<td style=\"text-align:center\"><a href=\"https://gitee.com/javadog-net\" target=\"_blank\">https://gitee.com/javadog-net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">GitHub</td>\n<td style=\"text-align:center\"><a href=\"https://github.com/javadog-net\" target=\"_blank\">https://github.com/javadog-net</a></td>\n</tr>\n</tbody>\n</table>\n', 0, 1, '2023-04-23 10:27:45', NULL, '2023-04-23 14:17:20', 1631537099054292993);
INSERT INTO `article` VALUES (1650034762884083713, 1648580798401482753, '【从零开始】Docker Desktop：听说你小子要玩我', 'Docker Desktop：听说你小子要玩我', '## 前言\n\n### 🍊缘由\n\n#### 捡起遗忘的Docker知识\n\n由于本狗近期**项目紧任务重**，高强度的搬砖导致**摸鱼时间下降**。在上线项目时，看到**运维大神**一系列骚操作，**docker+k8s的知识**如过眼云烟，忘得干净的很。所以想重新恶补一下**docker知识**，但又碍于**服务器资源有限**，只能从**本狗win10电脑进行安装Docker Desktop**，开始明日复明日的学习。\n\n***\n\n### 🎯主要目标\n\n#### 实现3大重点\n\n1.  win10系统**安装Docker Desktop**\n2.  在**Docker Desktop**中**实操Redis**\n3.  熟悉回顾**Docker主要语法命令**\n\n***\n\n### 🍯猜你喜欢\n\n#### 文章推荐\n\n[【事故】记一次意外把公司项目放到GitHub并被fork，如何使用DMCA下架政策保障隐私](https://mp.weixin.qq.com/s/RJXFKGOG7yndDHkiCZ_Egw)\n[【项目实战】SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序](https://mp.weixin.qq.com/s/t_qwF_HvkdW-6TI3sYUHrA)\n[【ChatGPT】手摸手，带你玩转ChatGPT](https://mp.weixin.qq.com/s/9wEelbTN6kaChkCQHmgJMQ)\n[【ChatGPT】SpringBoot+uniapp+uview2对接OpenAI，带你开发玩转ChatGPT](https://mp.weixin.qq.com/s/b19J36Eo3-ba7bHbWzoZYQ)\n[【模块分层】还不会SpringBoot项目模块分层？来这手把手教你！](https://mp.weixin.qq.com/s/fpkiNR2tj832a6VxZozwDg)\n\n***\n\n## 正文\n\n### 🏀前置问题\n\n#### 1. 是什么\n\n**Docker**在**Windows10**和**macOS操作系统**上的**官方安装方式**\n\n#### 2. 能干啥\n\n**Docker Desktop**是可以部署在windows运行docker的应用服务，其基于**windos的Hyper-V服务**和**WSL2内核**在windos上创建一个子系统(linux)，从而**实现在windows上运行docker**。\n\n#### 3. 怎么下\n\ndocker-desktop官网\n<https://www.docker.com/products/docker-desktop/>\n\n***\n\n### 🥝开始\n\n#### 1.下载\n\n访问官网<https://www.docker.com/products/docker-desktop/>，点击下载\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/2b181fb8f4584af1a6ac31391b4ec6cf~tplv-k3u1fbpfcp-zoom-1.image)\n\n#### 2.安装\n\n双击安装包后，出现配置选择，勾选两项并点击【Ok】，静等安装，大约需要3~5分钟\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/6059afcfe55c4bd2a9fcf735e810de32~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/cf20fa5a9c084e529442a3c7f4a17b6f~tplv-k3u1fbpfcp-zoom-1.image)![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ffbc4246705d46319714bb154bb3b700~tplv-k3u1fbpfcp-zoom-1.image)\n\n#### 3.操作\n\n双击桌面图标，打开后选择【Accept】，有些询问直接跳过点击【Skip】\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4432418f9ea64f9ab38c45f49bfd437e~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e87e595091184ee59ef3052eb3761042~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/616321f4d98146ec862585438db14282~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e8568da881ac402c8a4ca7b48bcf2255~tplv-k3u1fbpfcp-zoom-1.image)\n\n#### 4.修改镜像源\n\n点击【Extensions】右侧三个点，点击【Settings】，点击【Docker Engine】，切换镜像源\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/3a9887467bd742128386cd94abf65bca~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7c77da62d1b34b54a3ac8f7618c3a9ea~tplv-k3u1fbpfcp-zoom-1.image)\n**163镜像源**\n\n```json\n{\n  \"registry-mirrors\": [\n    \"http://hub-mirror.c.163.com\",\n  ],\n  \"insecure-registries\": [],\n  \"debug\": true,\n  \"experimental\": false\n}\n```\n\n**阿里云镜像源**\n\n> 登录阿里云，进入控制台，搜索“容器镜像服务”，找到加速地址\n\n<https://cr.console.aliyun.com/cn-qingdao/instances/mirrors>\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a84aee3debbb4935bca7507b10e950bb~tplv-k3u1fbpfcp-zoom-1.image)\n\n本人选择**阿里云和163**镜像源替换原始配置，并点击【Apply & restart】应用并重启\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/f6b14ab8ebec4d7dbeb8f8b29102e343~tplv-k3u1fbpfcp-zoom-1.image)\n\n#### 5.拉取镜像\n\n🌰以Redis为例，拉取镜像并启动\n\n*   在上方搜索框中输入redis，找官方镜像源，点击【pull】\n    ![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b57375559a1c4ccaad46c769b2dc829b~tplv-k3u1fbpfcp-zoom-1.image)\n*   拉取完成后，可以在Images中看到拉取后的redis镜像\n    ![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ba7f44b612924b4db69526b6dc8d64a4~tplv-k3u1fbpfcp-zoom-1.image)\n\n#### 6.启动容器\n\n*   点击我们刚才redis镜像中的【启动】按钮\n    ![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/33a445627b4a4ff0bdac6ca19b9aa829~tplv-k3u1fbpfcp-zoom-1.image)\n*   完善启动配置，填写【容器名】、【端口映射】，如有其余配置自行填写，然后点击【Run】\n    ![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/da0560e221bb40e28b4618e3c40ae64a~tplv-k3u1fbpfcp-zoom-1.image)\n*   启动完成后，查看启动的redis-01容器，及对应日志\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a5c419f95bd740eda5eb0efeb99f8480~tplv-k3u1fbpfcp-zoom-1.image)\n\n*   体验redis-cli，测试redis是否启动成功\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c5e20cd4dfbd4e51abf0a0f2faecd9af~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/1122b9fbd3184018b8d573be2387763c~tplv-k3u1fbpfcp-zoom-1.image)\n\n## 总结\n\n本文通过**Docker Desktop**从零开始，介绍了如何下载安装Docker Desktop，并切换镜像源。以实际**redis镜像**为例，实操拉取镜像，启动容器，手把手一步一步展示操作流程，希望能对大家有所帮助。\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/918e90cfe0bb41c989fa90acfdbfe8a4~tplv-k3u1fbpfcp-zoom-1.image)\n| JavaDog| 狗屋地址 |\n| :----:| :----: |\n| 个人博客 | <https://blog.javadog.net> |\n| 公众号 | <https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw> |\n| CSDN  | <https://blog.csdn.net/baidu_25986059> |\n| 掘金 | <https://juejin.cn/user/2172290706716775>|\n| 知乎 | <https://www.zhihu.com/people/JavaDog> |\n| 简书| <https://www.jianshu.com/u/1ff9c6bdb916> |\n| gitee|<https://gitee.com/javadog-net>  |\n| GitHub|<https://github.com/javadog-net>|\n', '<h2><a id=\"_0\"></a>前言</h2>\n<h3><a id=\"_2\"></a>🍊缘由</h3>\n<h4><a id=\"Docker_4\"></a>捡起遗忘的Docker知识</h4>\n<p>由于本狗近期<strong>项目紧任务重</strong>，高强度的搬砖导致<strong>摸鱼时间下降</strong>。在上线项目时，看到<strong>运维大神</strong>一系列骚操作，<strong>docker+k8s的知识</strong>如过眼云烟，忘得干净的很。所以想重新恶补一下<strong>docker知识</strong>，但又碍于<strong>服务器资源有限</strong>，只能从<strong>本狗win10电脑进行安装Docker Desktop</strong>，开始明日复明日的学习。</p>\n<hr />\n<h3><a id=\"_10\"></a>🎯主要目标</h3>\n<h4><a id=\"3_12\"></a>实现3大重点</h4>\n<ol>\n<li>win10系统<strong>安装Docker Desktop</strong></li>\n<li>在<strong>Docker Desktop</strong>中<strong>实操Redis</strong></li>\n<li>熟悉回顾<strong>Docker主要语法命令</strong></li>\n</ol>\n<hr />\n<h3><a id=\"_20\"></a>🍯猜你喜欢</h3>\n<h4><a id=\"_22\"></a>文章推荐</h4>\n<p><a href=\"https://mp.weixin.qq.com/s/RJXFKGOG7yndDHkiCZ_Egw\" target=\"_blank\">【事故】记一次意外把公司项目放到GitHub并被fork，如何使用DMCA下架政策保障隐私</a><br />\n<a href=\"https://mp.weixin.qq.com/s/t_qwF_HvkdW-6TI3sYUHrA\" target=\"_blank\">【项目实战】SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序</a><br />\n<a href=\"https://mp.weixin.qq.com/s/9wEelbTN6kaChkCQHmgJMQ\" target=\"_blank\">【ChatGPT】手摸手，带你玩转ChatGPT</a><br />\n<a href=\"https://mp.weixin.qq.com/s/b19J36Eo3-ba7bHbWzoZYQ\" target=\"_blank\">【ChatGPT】SpringBoot+uniapp+uview2对接OpenAI，带你开发玩转ChatGPT</a><br />\n<a href=\"https://mp.weixin.qq.com/s/fpkiNR2tj832a6VxZozwDg\" target=\"_blank\">【模块分层】还不会SpringBoot项目模块分层？来这手把手教你！</a></p>\n<hr />\n<h2><a id=\"_32\"></a>正文</h2>\n<h3><a id=\"_34\"></a>🏀前置问题</h3>\n<h4><a id=\"1__36\"></a>1. 是什么</h4>\n<p><strong>Docker</strong>在<strong>Windows10</strong>和<strong>macOS操作系统</strong>上的<strong>官方安装方式</strong></p>\n<h4><a id=\"2__40\"></a>2. 能干啥</h4>\n<p><strong>Docker Desktop</strong>是可以部署在windows运行docker的应用服务，其基于<strong>windos的Hyper-V服务</strong>和<strong>WSL2内核</strong>在windos上创建一个子系统(linux)，从而<strong>实现在windows上运行docker</strong>。</p>\n<h4><a id=\"3__44\"></a>3. 怎么下</h4>\n<p>docker-desktop官网<br />\n<a href=\"https://www.docker.com/products/docker-desktop/\" target=\"_blank\">https://www.docker.com/products/docker-desktop/</a></p>\n<hr />\n<h3><a id=\"_51\"></a>🥝开始</h3>\n<h4><a id=\"1_53\"></a>1.下载</h4>\n<p>访问官网<a href=\"https://www.docker.com/products/docker-desktop/\" target=\"_blank\">https://www.docker.com/products/docker-desktop/</a>，点击下载<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/2b181fb8f4584af1a6ac31391b4ec6cf~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h4><a id=\"2_58\"></a>2.安装</h4>\n<p>双击安装包后，出现配置选择，勾选两项并点击【Ok】，静等安装，大约需要3~5分钟<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/6059afcfe55c4bd2a9fcf735e810de32~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/cf20fa5a9c084e529442a3c7f4a17b6f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ffbc4246705d46319714bb154bb3b700~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h4><a id=\"3_64\"></a>3.操作</h4>\n<p>双击桌面图标，打开后选择【Accept】，有些询问直接跳过点击【Skip】</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4432418f9ea64f9ab38c45f49bfd437e~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e87e595091184ee59ef3052eb3761042~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/616321f4d98146ec862585438db14282~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/e8568da881ac402c8a4ca7b48bcf2255~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h4><a id=\"4_73\"></a>4.修改镜像源</h4>\n<p>点击【Extensions】右侧三个点，点击【Settings】，点击【Docker Engine】，切换镜像源<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/3a9887467bd742128386cd94abf65bca~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7c77da62d1b34b54a3ac8f7618c3a9ea~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<strong>163镜像源</strong></p>\n<pre><div class=\"hljs\"><code class=\"lang-json\"><span class=\"hljs-punctuation\">{</span>\n  <span class=\"hljs-attr\">&quot;registry-mirrors&quot;</span><span class=\"hljs-punctuation\">:</span> <span class=\"hljs-punctuation\">[</span>\n    <span class=\"hljs-string\">&quot;http://hub-mirror.c.163.com&quot;</span><span class=\"hljs-punctuation\">,</span>\n  <span class=\"hljs-punctuation\">]</span><span class=\"hljs-punctuation\">,</span>\n  <span class=\"hljs-attr\">&quot;insecure-registries&quot;</span><span class=\"hljs-punctuation\">:</span> <span class=\"hljs-punctuation\">[</span><span class=\"hljs-punctuation\">]</span><span class=\"hljs-punctuation\">,</span>\n  <span class=\"hljs-attr\">&quot;debug&quot;</span><span class=\"hljs-punctuation\">:</span> <span class=\"hljs-keyword\">true</span><span class=\"hljs-punctuation\">,</span>\n  <span class=\"hljs-attr\">&quot;experimental&quot;</span><span class=\"hljs-punctuation\">:</span> <span class=\"hljs-keyword\">false</span>\n<span class=\"hljs-punctuation\">}</span>\n</code></div></pre>\n<p><strong>阿里云镜像源</strong></p>\n<blockquote>\n<p>登录阿里云，进入控制台，搜索“容器镜像服务”，找到加速地址</p>\n</blockquote>\n<p><a href=\"https://cr.console.aliyun.com/cn-qingdao/instances/mirrors\" target=\"_blank\">https://cr.console.aliyun.com/cn-qingdao/instances/mirrors</a><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a84aee3debbb4935bca7507b10e950bb~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<p>本人选择<strong>阿里云和163</strong>镜像源替换原始配置，并点击【Apply &amp; restart】应用并重启</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/f6b14ab8ebec4d7dbeb8f8b29102e343~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h4><a id=\"5_102\"></a>5.拉取镜像</h4>\n<p>🌰以Redis为例，拉取镜像并启动</p>\n<ul>\n<li>在上方搜索框中输入redis，找官方镜像源，点击【pull】<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b57375559a1c4ccaad46c769b2dc829b~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></li>\n<li>拉取完成后，可以在Images中看到拉取后的redis镜像<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ba7f44b612924b4db69526b6dc8d64a4~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></li>\n</ul>\n<h4><a id=\"6_111\"></a>6.启动容器</h4>\n<ul>\n<li>点击我们刚才redis镜像中的【启动】按钮<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/33a445627b4a4ff0bdac6ca19b9aa829~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></li>\n<li>完善启动配置，填写【容器名】、【端口映射】，如有其余配置自行填写，然后点击【Run】<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/da0560e221bb40e28b4618e3c40ae64a~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></li>\n<li>启动完成后，查看启动的redis-01容器，及对应日志</li>\n</ul>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a5c419f95bd740eda5eb0efeb99f8480~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<ul>\n<li>体验redis-cli，测试redis是否启动成功</li>\n</ul>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c5e20cd4dfbd4e51abf0a0f2faecd9af~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/1122b9fbd3184018b8d573be2387763c~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h2><a id=\"_126\"></a>总结</h2>\n<p>本文通过<strong>Docker Desktop</strong>从零开始，介绍了如何下载安装Docker Desktop，并切换镜像源。以实际<strong>redis镜像</strong>为例，实操拉取镜像，启动容器，手把手一步一步展示操作流程，希望能对大家有所帮助。</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/918e90cfe0bb41c989fa90acfdbfe8a4~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<table>\n<thead>\n<tr>\n<th style=\"text-align:center\">JavaDog</th>\n<th style=\"text-align:center\">狗屋地址</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td style=\"text-align:center\">个人博客</td>\n<td style=\"text-align:center\"><a href=\"https://blog.javadog.net\" target=\"_blank\">https://blog.javadog.net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">公众号</td>\n<td style=\"text-align:center\"><a href=\"https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw\" target=\"_blank\">https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">CSDN</td>\n<td style=\"text-align:center\"><a href=\"https://blog.csdn.net/baidu_25986059\" target=\"_blank\">https://blog.csdn.net/baidu_25986059</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">掘金</td>\n<td style=\"text-align:center\"><a href=\"https://juejin.cn/user/2172290706716775\" target=\"_blank\">https://juejin.cn/user/2172290706716775</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">知乎</td>\n<td style=\"text-align:center\"><a href=\"https://www.zhihu.com/people/JavaDog\" target=\"_blank\">https://www.zhihu.com/people/JavaDog</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">简书</td>\n<td style=\"text-align:center\"><a href=\"https://www.jianshu.com/u/1ff9c6bdb916\" target=\"_blank\">https://www.jianshu.com/u/1ff9c6bdb916</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">gitee</td>\n<td style=\"text-align:center\"><a href=\"https://gitee.com/javadog-net\" target=\"_blank\">https://gitee.com/javadog-net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">GitHub</td>\n<td style=\"text-align:center\"><a href=\"https://github.com/javadog-net\" target=\"_blank\">https://github.com/javadog-net</a></td>\n</tr>\n</tbody>\n</table>\n', 0, 1, '2023-04-23 15:11:58', 1631537099054292993, '2023-04-23 15:11:58', NULL);
INSERT INTO `article` VALUES (1650035735006310402, 1650034904580255745, '【微信网页授权】SpringBoot+uniapp实现网页授权获取用户基本信息', 'SpringBoot+uniapp实现网页授权获取用户基本信息', '## 前言\n### 🍊缘由\n起因于本狗上一个项目本打算采用**微信公众号网页授权登录**做用户鉴权，但最终因公众号是**未认证的订阅号**，**无权限获取用户信息**，所以改变思路，采用登录注册方式实现用户区分。但在开发中，学习了**微信网页授权登录**流程，特此分享，带你手把手操作，让我们一起捋清授权的逻辑。\n*******\n### ⏲️建议阅读时长\n约20分钟\n*******\n### 🎯主要目标\n1. **测试公众号**实现**网页授权**并获取**用户基本信息**\n2. **灵活掌握微信接口文档**，熟练对接相关接口\n3. 整理授权逻辑，前后端对接熟悉流程\n******\n\n### 👨‍🎓试用人群\n1. 对于**微信公众号网页授权**流程模糊或初次接触\n2. 做过相关流程但想回顾熟悉复盘\n******\n### 🎁快速链接\n公众号：**JavaDog程序狗**\n**在公众号，发送【wx】 ，无任何套路即可获得**\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/68fe503c323845c5b20d29cc385492e4~tplv-k3u1fbpfcp-zoom-1.image)\n\n\n\n******\n###  🍯猜你喜欢\n|序号| 分类| 文章描述 | \n| --- | ----- |  ----- |\n| 1 | 项目实战 | [SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序](https://blog.csdn.net/baidu_25986059/article/details/129263139) |\n\n### 🍩水图\n![请添加图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/95e596f301254f99820935ecbb62de35~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n## 正文\n### 📝准备\n#### 1.服务号/订阅号(已认证)\n> 网页授权接口权限只有**服务号**或者**已认证的订阅号**有权限，也就是需要每年缴纳300块钱费用认证过才会有权限，否则无法在正式公众号调用此接口。 \n> 🚨 切记无权限时调用会提示【微信公众号授权scope参数错误或没有scope权限】，只可在**测试公众号进行调试**！！！\n\n\n#### 2.开发工具\n|序号| 分类| 工具| \n| --- | ----- |  ----- |\n| 1 | 后端 |  IDEA |\n| 2 | 前端 | uniapp  |\n| 3 | 前端 | 微信开发者工具|\n\n#### 3.接口文档\n\n[微信官方公众号文档](https://developers.weixin.qq.com/doc/offiaccount/OA_Web_Apps/Wechat_webpage_authorization.html)\n\n#### 4.网页授权获取用户基本信息配置\n> 以测试号进行展示操作步骤\n\n##### 在公众号左侧菜单【开发工具】=》【公众平台测试帐号】\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/249afad807604c98bd8c78752f51b309~tplv-k3u1fbpfcp-zoom-1.image)\n##### 在网页服务-网页账号-修改\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a869467da5ab4553a82955b40c182b9c~tplv-k3u1fbpfcp-zoom-1.image)\n##### 修改OAuth2.0网页授权【授权回调页面域名】\n> **切记不要带http**，此处地址填写本地ip即可。可在键盘通过【win+R】，输入【cmd】，确定后输入ipconfig，复制出IPv4地址即可。或者启动uniapp后，控制台打印的地址，**以上方法仅限本地调试**。\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/88b53cbbb8fd48a3bc4ecf5bea32e8f7~tplv-k3u1fbpfcp-zoom-1.image)\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c3467a6748664f199499ee0495b3f686~tplv-k3u1fbpfcp-zoom-1.image)\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7d6d2c4de6ec4ee1b87c25474def0952~tplv-k3u1fbpfcp-zoom-1.image)\n\n\n******\n### 🔛问题剖析\n#### 1.什么是网页授权？\n##### 官网解释\n> 如果用户在**微信客户端**中访问**第三方网页**，公众号可以通过微信**网页授权机制**，来获取用户基本信息，进而实现业务逻辑。\n\n##### 简单理解\n> 通俗来说，就是我们通过**微信内置浏览器**去访问其他网站时，可以通过**微信授权策略**，在目标网站可以获取微信用户的基本信息，如昵称，头像等\n\n##### 举个栗子 🌰\n>  通过**微信内打开csdn分享链接**，当跳转到csdn网页时，csdn就可以通过**网页授权**，直接拿到我们微信的昵称，头像信息等，完成**自主授权注册**\n\n#### 2.网页授权流程步骤？\n##### 1. 用户同意授权，获取code\n> 前端引导用户进入授权页面同意授权，获取code\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9b9704739e5b4e4aba94a609603a5281~tplv-k3u1fbpfcp-zoom-1.image)\n##### 2. 通过 code 换取网页授权access_token\n> 通过前端获取的code，**调用我们自己服务器接口**，通过**后台使用code调取微信接口**，换取**access_token(网页授权接口调用凭证)**，access_token是调用用户信息及其他接口所必需的参数\n\n##### 3. 刷新access_token（如果需要）\n> 由于**access_token**拥有较短的**有效期**，当**access_token超时**后，可以使用**refresh_token**进行刷新，refresh_token有效期为30天，当refresh_token失效之后，需要**用户重新授权**\n\n##### 4. 拉取用户信息\n> 开发者可以通过**access_token**和 **openid** 拉取用户信息\n\n🎯 如果看不懂不要划走，下面才是重点讲解\n🎯 如果看不懂不要划走，下面才是重点讲解\n🎯 如果看不懂不要划走，下面才是重点讲解\n******\n\n### 👼开始\n#### 简易流程图\n> 为了方便理解，画了一个简易流程图，有疑惑别急，下面还有现实列子解释版\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/bd3cc64d9739428e827fb0ee6d9f54ab~tplv-k3u1fbpfcp-zoom-1.image)\n\n> 举个实际购买**苹果手机**例子，可能不太贴合，但有助与理解\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b49a49cc87174affb2b0c1f971fbde41~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### 步骤\n> 根据下图进行步骤分析\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/064c3020e03740508f26627b35af7d17~tplv-k3u1fbpfcp-zoom-1.image)\n##### 1.步骤一\n\n 前端uniapp中**新建授权页**\n\n> 页面进入后，onLoad直接跳转微信接口地址，微信方通过appId获取code，并**携带code**根据**redirect_uri返回到当前页面**，此刻**地址栏中就包含着code信息**\n\n**跳转前**\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/855631b33bf84c03a616921bf7ef5f7e~tplv-k3u1fbpfcp-zoom-1.image)\n\n**跳转后**\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/455a06460e15495b932bef57742c3487~tplv-k3u1fbpfcp-zoom-1.image)\n\n关键代码\n\n```js\n//通过微信官方接口获取code之后，会重新刷新设置的回调地址【redirect_uri】,之后会继续进入到onload()方法\nlocation.href = \"https://open.weixin.qq.com/connect/oauth2/authorize?appid=\" + appid + \"&redirect_uri=\" +\nlocal + \"&response_type=code&scope=snsapi_userinfo&state=STATE&connect_redirect=1#wechat_redirect\"\"\n```\n**appid** ：公众号对应的appId\n**redirect_uri**：页面回跳地址，也就是当前页面的地址，此地址也就是在上方**准备4**中OAuth2.0网页授权【授权回调页面域名】填入的值。微信根据这个回调地址进行携带code回跳。\n\n其他参数可看官方参数说明\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d299334767994f25b46b9fa8ef3a2651~tplv-k3u1fbpfcp-zoom-1.image)\n******\n##### 2.步骤二\n1.后端提供接口，**入参**为上一步的**code**，**通过code**调取微信接口**换取access_token**。此处后端使用了[weixin-java-mp](https://github.com/Wechat-Group/WxJava)组件，方便对接微信接口。\n\n```java\n@Api(tags = \"网页授权控制器\")\n@RestController\n@RequestMapping(\"/login\")\npublic class AuthController {\n\n    @Resource\n    private UserService userService;\n\n    @ApiOperation(value = \"网页授权获取用户基本信息\", notes = \"网页授权获取用户基本信息\")\n    @GetMapping\n    public ResponseData login(@RequestParam String code) throws WxErrorException {\n        return userService.auth(code);\n    }\n}\n```\n\n2.前端带着code，调用1中的/login接口\n```js\nconst res = await this.$api.login({\n		code\n	})\n	if (!res.success) {\n		uni.$u.toast(res.message)\n		return\n	}\n	// 用户信息\n	this.userInfo = res.data;\n}\n```\n******\n##### 3.步骤三\n将步骤二后端拿到的**access_token**，调用微信**获取用户信息**，将用户信息返回给前端\n\n```java\n WxOAuth2AccessToken accessToken;\n        WxMpUser wxMpUser;\n        try {\n        	// 获取accessToken \n            accessToken = wxMpService.getOAuth2Service().getAccessToken(code);\n            log.info(\"AccessToken={}\" + accessToken);\n            // 获取用户信息\n            wxMpUser = wxMpService.getUserService().userInfo(accessToken.getOpenId());\n            log.info(\"wxMpUser={}\", JSONUtil.toJsonStr(wxMpUser));\n        } catch (WxErrorException e) {\n            return ResponseData.error(e.getMessage());\n        }\n        return ResponseData.success(wxMpUser);\n```\n******\n\n### ❌常见错误\n#### 错误1：redirect_uri 参数错误\n> 微信中【OAuth2.0网页授权】-**授权回调页面域名配置错误**\n\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/682c922564aa475f84ee227c2ea88b7f~tplv-k3u1fbpfcp-zoom-1.image)\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ac65218982d241be8d24449fcf2cb3fe~tplv-k3u1fbpfcp-zoom-1.image)\n#### 错误2：oauth_code已使用\n> **code每次使用换取access_token后便失效**，再次调用则会报错，请**根据自己业务**，进行缓存比较，**防止重复调用**\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/6f81c3a97b6d4510994e810c2a6f90cc~tplv-k3u1fbpfcp-zoom-1.image)\n## 总结\n本文虽然篇幅长，但是实现功能简单，主要目的是展示**网页授权**的流程与思路，对于微信对接，本人建议多查看**微信官方文档**，写的很详细，通读过几遍后就会对疑问点有些许领悟，希望能对大家有所帮助。\n\n![](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7bc0f05773c2436c84562cc0b3e61a70~tplv-k3u1fbpfcp-zoom-1.image)\n\n\n| JavaDog| 狗屋地址 |\n| :----:| :----: | \n| 个人博客 | [https://blog.javadog.net](https://blog.javadog.net) | \n| 公众号 | [https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw](https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw) | \n| CSDN  | [https://blog.csdn.net/baidu_25986059](https://blog.csdn.net/baidu_25986059) | \n| 掘金 | [https://juejin.cn/user/2172290706716775](https://juejin.cn/user/2172290706716775)| \n| 知乎 | [https://www.zhihu.com/people/JavaDog](https://www.zhihu.com/people/JavaDog) | \n| 简书| [https://www.jianshu.com/u/1ff9c6bdb916](https://www.jianshu.com/u/1ff9c6bdb916) | \n| gitee|[https://gitee.com/javadog-net](https://gitee.com/javadog-net)  | \n| GitHub|[https://github.com/javadog-net](https://github.com/javadog-net)| \n', '<h2><a id=\"_0\"></a>前言</h2>\n<h3><a id=\"_1\"></a>🍊缘由</h3>\n<p>起因于本狗上一个项目本打算采用<strong>微信公众号网页授权登录</strong>做用户鉴权，但最终因公众号是<strong>未认证的订阅号</strong>，<strong>无权限获取用户信息</strong>，所以改变思路，采用登录注册方式实现用户区分。但在开发中，学习了<strong>微信网页授权登录</strong>流程，特此分享，带你手把手操作，让我们一起捋清授权的逻辑。</p>\n<hr />\n<h3><a id=\"_4\"></a>⏲️建议阅读时长</h3>\n<p>约20分钟</p>\n<hr />\n<h3><a id=\"_7\"></a>🎯主要目标</h3>\n<ol>\n<li><strong>测试公众号</strong>实现<strong>网页授权</strong>并获取<strong>用户基本信息</strong></li>\n<li><strong>灵活掌握微信接口文档</strong>，熟练对接相关接口</li>\n<li>整理授权逻辑，前后端对接熟悉流程</li>\n</ol>\n<hr />\n<h3><a id=\"_13\"></a>👨‍🎓试用人群</h3>\n<ol>\n<li>对于<strong>微信公众号网页授权</strong>流程模糊或初次接触</li>\n<li>做过相关流程但想回顾熟悉复盘</li>\n</ol>\n<hr />\n<h3><a id=\"_17\"></a>🎁快速链接</h3>\n<p>公众号：<strong>JavaDog程序狗</strong><br />\n<strong>在公众号，发送【wx】 ，无任何套路即可获得</strong></p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/68fe503c323845c5b20d29cc385492e4~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h3><a id=\"_26\"></a>🍯猜你喜欢</h3>\n<table>\n<thead>\n<tr>\n<th>序号</th>\n<th>分类</th>\n<th>文章描述</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>1</td>\n<td>项目实战</td>\n<td><a href=\"https://blog.csdn.net/baidu_25986059/article/details/129263139\" target=\"_blank\">SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序</a></td>\n</tr>\n</tbody>\n</table>\n<h3><a id=\"_31\"></a>🍩水图</h3>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/95e596f301254f99820935ecbb62de35~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"请添加图片描述\" /></p>\n<hr />\n<h2><a id=\"_35\"></a>正文</h2>\n<h3><a id=\"_36\"></a>📝准备</h3>\n<h4><a id=\"1_37\"></a>1.服务号/订阅号(已认证)</h4>\n<blockquote>\n<p>网页授权接口权限只有<strong>服务号</strong>或者<strong>已认证的订阅号</strong>有权限，也就是需要每年缴纳300块钱费用认证过才会有权限，否则无法在正式公众号调用此接口。<br />\n🚨 切记无权限时调用会提示【微信公众号授权scope参数错误或没有scope权限】，只可在<strong>测试公众号进行调试</strong>！！！</p>\n</blockquote>\n<h4><a id=\"2_42\"></a>2.开发工具</h4>\n<table>\n<thead>\n<tr>\n<th>序号</th>\n<th>分类</th>\n<th>工具</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>1</td>\n<td>后端</td>\n<td>IDEA</td>\n</tr>\n<tr>\n<td>2</td>\n<td>前端</td>\n<td>uniapp</td>\n</tr>\n<tr>\n<td>3</td>\n<td>前端</td>\n<td>微信开发者工具</td>\n</tr>\n</tbody>\n</table>\n<h4><a id=\"3_49\"></a>3.接口文档</h4>\n<p><a href=\"https://developers.weixin.qq.com/doc/offiaccount/OA_Web_Apps/Wechat_webpage_authorization.html\" target=\"_blank\">微信官方公众号文档</a></p>\n<h4><a id=\"4_53\"></a>4.网页授权获取用户基本信息配置</h4>\n<blockquote>\n<p>以测试号进行展示操作步骤</p>\n</blockquote>\n<h5><a id=\"_56\"></a>在公众号左侧菜单【开发工具】=》【公众平台测试帐号】</h5>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/249afad807604c98bd8c78752f51b309~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h5><a id=\"_58\"></a>在网页服务-网页账号-修改</h5>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/a869467da5ab4553a82955b40c182b9c~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h5><a id=\"OAuth20_60\"></a>修改OAuth2.0网页授权【授权回调页面域名】</h5>\n<blockquote>\n<p><strong>切记不要带http</strong>，此处地址填写本地ip即可。可在键盘通过【win+R】，输入【cmd】，确定后输入ipconfig，复制出IPv4地址即可。或者启动uniapp后，控制台打印的地址，<strong>以上方法仅限本地调试</strong>。</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/88b53cbbb8fd48a3bc4ecf5bea32e8f7~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c3467a6748664f199499ee0495b3f686~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7d6d2c4de6ec4ee1b87c25474def0952~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<hr />\n<h3><a id=\"_70\"></a>🔛问题剖析</h3>\n<h4><a id=\"1_71\"></a>1.什么是网页授权？</h4>\n<h5><a id=\"_72\"></a>官网解释</h5>\n<blockquote>\n<p>如果用户在<strong>微信客户端</strong>中访问<strong>第三方网页</strong>，公众号可以通过微信<strong>网页授权机制</strong>，来获取用户基本信息，进而实现业务逻辑。</p>\n</blockquote>\n<h5><a id=\"_75\"></a>简单理解</h5>\n<blockquote>\n<p>通俗来说，就是我们通过<strong>微信内置浏览器</strong>去访问其他网站时，可以通过<strong>微信授权策略</strong>，在目标网站可以获取微信用户的基本信息，如昵称，头像等</p>\n</blockquote>\n<h5><a id=\"__78\"></a>举个栗子 🌰</h5>\n<blockquote>\n<p>通过<strong>微信内打开csdn分享链接</strong>，当跳转到csdn网页时，csdn就可以通过<strong>网页授权</strong>，直接拿到我们微信的昵称，头像信息等，完成<strong>自主授权注册</strong></p>\n</blockquote>\n<h4><a id=\"2_81\"></a>2.网页授权流程步骤？</h4>\n<h5><a id=\"1_code_82\"></a>1. 用户同意授权，获取code</h5>\n<blockquote>\n<p>前端引导用户进入授权页面同意授权，获取code</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9b9704739e5b4e4aba94a609603a5281~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h5><a id=\"2__code_access_token_86\"></a>2. 通过 code 换取网页授权access_token</h5>\n<blockquote>\n<p>通过前端获取的code，<strong>调用我们自己服务器接口</strong>，通过<strong>后台使用code调取微信接口</strong>，换取<strong>access_token(网页授权接口调用凭证)</strong>，access_token是调用用户信息及其他接口所必需的参数</p>\n</blockquote>\n<h5><a id=\"3_access_token_89\"></a>3. 刷新access_token（如果需要）</h5>\n<blockquote>\n<p>由于<strong>access_token</strong>拥有较短的<strong>有效期</strong>，当<strong>access_token超时</strong>后，可以使用<strong>refresh_token</strong>进行刷新，refresh_token有效期为30天，当refresh_token失效之后，需要<strong>用户重新授权</strong></p>\n</blockquote>\n<h5><a id=\"4__92\"></a>4. 拉取用户信息</h5>\n<blockquote>\n<p>开发者可以通过<strong>access_token</strong>和 <strong>openid</strong> 拉取用户信息</p>\n</blockquote>\n<p>🎯 如果看不懂不要划走，下面才是重点讲解<br />\n🎯 如果看不懂不要划走，下面才是重点讲解<br />\n🎯 如果看不懂不要划走，下面才是重点讲解</p>\n<hr />\n<h3><a id=\"_100\"></a>👼开始</h3>\n<h4><a id=\"_101\"></a>简易流程图</h4>\n<blockquote>\n<p>为了方便理解，画了一个简易流程图，有疑惑别急，下面还有现实列子解释版</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/bd3cc64d9739428e827fb0ee6d9f54ab~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<blockquote>\n<p>举个实际购买<strong>苹果手机</strong>例子，可能不太贴合，但有助与理解</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b49a49cc87174affb2b0c1f971fbde41~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<hr />\n<h4><a id=\"_110\"></a>步骤</h4>\n<blockquote>\n<p>根据下图进行步骤分析</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/064c3020e03740508f26627b35af7d17~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h5><a id=\"1_114\"></a>1.步骤一</h5>\n<p>前端uniapp中<strong>新建授权页</strong></p>\n<blockquote>\n<p>页面进入后，onLoad直接跳转微信接口地址，微信方通过appId获取code，并<strong>携带code</strong>根据<strong>redirect_uri返回到当前页面</strong>，此刻<strong>地址栏中就包含着code信息</strong></p>\n</blockquote>\n<p><strong>跳转前</strong><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/855631b33bf84c03a616921bf7ef5f7e~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<p><strong>跳转后</strong><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/455a06460e15495b932bef57742c3487~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<p>关键代码</p>\n<pre><div class=\"hljs\"><code class=\"lang-js\"><span class=\"hljs-comment\">//通过微信官方接口获取code之后，会重新刷新设置的回调地址【redirect_uri】,之后会继续进入到onload()方法</span>\nlocation.<span class=\"hljs-property\">href</span> = <span class=\"hljs-string\">&quot;https://open.weixin.qq.com/connect/oauth2/authorize?appid=&quot;</span> + appid + <span class=\"hljs-string\">&quot;&amp;redirect_uri=&quot;</span> +\nlocal + <span class=\"hljs-string\">&quot;&amp;response_type=code&amp;scope=snsapi_userinfo&amp;state=STATE&amp;connect_redirect=1#wechat_redirect&quot;</span><span class=\"hljs-string\">&quot;\n</span></code></div></pre>\n<p><strong>appid</strong> ：公众号对应的appId<br />\n<strong>redirect_uri</strong>：页面回跳地址，也就是当前页面的地址，此地址也就是在上方<strong>准备4</strong>中OAuth2.0网页授权【授权回调页面域名】填入的值。微信根据这个回调地址进行携带code回跳。</p>\n<p>其他参数可看官方参数说明</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d299334767994f25b46b9fa8ef3a2651~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<hr />\n<h5><a id=\"2_140\"></a>2.步骤二</h5>\n<p>1.后端提供接口，<strong>入参</strong>为上一步的<strong>code</strong>，<strong>通过code</strong>调取微信接口<strong>换取access_token</strong>。此处后端使用了<a href=\"https://github.com/Wechat-Group/WxJava\" target=\"_blank\">weixin-java-mp</a>组件，方便对接微信接口。</p>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-meta\">@Api(tags = &quot;网页授权控制器&quot;)</span>\n<span class=\"hljs-meta\">@RestController</span>\n<span class=\"hljs-meta\">@RequestMapping(&quot;/login&quot;)</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> <span class=\"hljs-title class_\">AuthController</span> {\n\n    <span class=\"hljs-meta\">@Resource</span>\n    <span class=\"hljs-keyword\">private</span> UserService userService;\n\n    <span class=\"hljs-meta\">@ApiOperation(value = &quot;网页授权获取用户基本信息&quot;, notes = &quot;网页授权获取用户基本信息&quot;)</span>\n    <span class=\"hljs-meta\">@GetMapping</span>\n    <span class=\"hljs-keyword\">public</span> ResponseData <span class=\"hljs-title function_\">login</span><span class=\"hljs-params\">(<span class=\"hljs-meta\">@RequestParam</span> String code)</span> <span class=\"hljs-keyword\">throws</span> WxErrorException {\n        <span class=\"hljs-keyword\">return</span> userService.auth(code);\n    }\n}\n</code></div></pre>\n<p>2.前端带着code，调用1中的/login接口</p>\n<pre><div class=\"hljs\"><code class=\"lang-js\"><span class=\"hljs-keyword\">const</span> res = <span class=\"hljs-keyword\">await</span> <span class=\"hljs-variable language_\">this</span>.<span class=\"hljs-property\">$api</span>.<span class=\"hljs-title function_\">login</span>({\n		code\n	})\n	<span class=\"hljs-keyword\">if</span> (!res.<span class=\"hljs-property\">success</span>) {\n		uni.<span class=\"hljs-property\">$u</span>.<span class=\"hljs-title function_\">toast</span>(res.<span class=\"hljs-property\">message</span>)\n		<span class=\"hljs-keyword\">return</span>\n	}\n	<span class=\"hljs-comment\">// 用户信息</span>\n	<span class=\"hljs-variable language_\">this</span>.<span class=\"hljs-property\">userInfo</span> = res.<span class=\"hljs-property\">data</span>;\n}\n</code></div></pre>\n<hr />\n<h5><a id=\"3_174\"></a>3.步骤三</h5>\n<p>将步骤二后端拿到的<strong>access_token</strong>，调用微信<strong>获取用户信息</strong>，将用户信息返回给前端</p>\n<pre><div class=\"hljs\"><code class=\"lang-java\"> WxOAuth2AccessToken accessToken;\n        WxMpUser wxMpUser;\n        <span class=\"hljs-keyword\">try</span> {\n        	<span class=\"hljs-comment\">// 获取accessToken </span>\n            accessToken = wxMpService.getOAuth2Service().getAccessToken(code);\n            log.info(<span class=\"hljs-string\">&quot;AccessToken={}&quot;</span> + accessToken);\n            <span class=\"hljs-comment\">// 获取用户信息</span>\n            wxMpUser = wxMpService.getUserService().userInfo(accessToken.getOpenId());\n            log.info(<span class=\"hljs-string\">&quot;wxMpUser={}&quot;</span>, JSONUtil.toJsonStr(wxMpUser));\n        } <span class=\"hljs-keyword\">catch</span> (WxErrorException e) {\n            <span class=\"hljs-keyword\">return</span> ResponseData.error(e.getMessage());\n        }\n        <span class=\"hljs-keyword\">return</span> ResponseData.success(wxMpUser);\n</code></div></pre>\n<hr />\n<h3><a id=\"_194\"></a>❌常见错误</h3>\n<h4><a id=\"1redirect_uri__195\"></a>错误1：redirect_uri 参数错误</h4>\n<blockquote>\n<p>微信中【OAuth2.0网页授权】-<strong>授权回调页面域名配置错误</strong></p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/682c922564aa475f84ee227c2ea88b7f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ac65218982d241be8d24449fcf2cb3fe~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h4><a id=\"2oauth_code_201\"></a>错误2：oauth_code已使用</h4>\n<blockquote>\n<p><strong>code每次使用换取access_token后便失效</strong>，再次调用则会报错，请<strong>根据自己业务</strong>，进行缓存比较，<strong>防止重复调用</strong></p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/6f81c3a97b6d4510994e810c2a6f90cc~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<h2><a id=\"_205\"></a>总结</h2>\n<p>本文虽然篇幅长，但是实现功能简单，主要目的是展示<strong>网页授权</strong>的流程与思路，对于微信对接，本人建议多查看<strong>微信官方文档</strong>，写的很详细，通读过几遍后就会对疑问点有些许领悟，希望能对大家有所帮助。</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7bc0f05773c2436c84562cc0b3e61a70~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"\" /></p>\n<table>\n<thead>\n<tr>\n<th style=\"text-align:center\">JavaDog</th>\n<th style=\"text-align:center\">狗屋地址</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td style=\"text-align:center\">个人博客</td>\n<td style=\"text-align:center\"><a href=\"https://blog.javadog.net\" target=\"_blank\">https://blog.javadog.net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">公众号</td>\n<td style=\"text-align:center\"><a href=\"https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw\" target=\"_blank\">https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">CSDN</td>\n<td style=\"text-align:center\"><a href=\"https://blog.csdn.net/baidu_25986059\" target=\"_blank\">https://blog.csdn.net/baidu_25986059</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">掘金</td>\n<td style=\"text-align:center\"><a href=\"https://juejin.cn/user/2172290706716775\" target=\"_blank\">https://juejin.cn/user/2172290706716775</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">知乎</td>\n<td style=\"text-align:center\"><a href=\"https://www.zhihu.com/people/JavaDog\" target=\"_blank\">https://www.zhihu.com/people/JavaDog</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">简书</td>\n<td style=\"text-align:center\"><a href=\"https://www.jianshu.com/u/1ff9c6bdb916\" target=\"_blank\">https://www.jianshu.com/u/1ff9c6bdb916</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">gitee</td>\n<td style=\"text-align:center\"><a href=\"https://gitee.com/javadog-net\" target=\"_blank\">https://gitee.com/javadog-net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">GitHub</td>\n<td style=\"text-align:center\"><a href=\"https://github.com/javadog-net\" target=\"_blank\">https://github.com/javadog-net</a></td>\n</tr>\n</tbody>\n</table>\n', 0, 1, '2023-04-23 15:15:50', 1631537099054292993, '2023-04-23 15:15:50', NULL);
INSERT INTO `article` VALUES (1650036050380222465, 1648580798401482753, '【项目实战】SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序', 'SpringBoot+uniapp+uview2打造一个企业黑红名单吐槽小程序', '---\ntheme: vuepress\n---\n<div align=\"center\">\n    <p align=\"center\">\n        <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d8d26d62b5024e03a447ef5aae7bb328~tplv-k3u1fbpfcp-zoom-1.image\" height=\"150\" alt=\"logo\"/>\n    </p>\n    <h1 align=\"center\" style=\"margin: 30px 0 30px; font-weight: bold;\">避坑宝 v1.0.0</h1>\n	<h4 align=\"center\">基于SpringBoot+uniapp企业黑红名单吐槽小程序</h4>\n</div>\n\n## 📚项目介绍\n### 避坑宝\n【避坑宝】**企业黑红名单吐槽小程序**是一个具有**吐槽发布企业信息的一个平台**，言论自由，评判自定，便于我们打工人分辨企业好坏。技术栈基于**SpringBoot+MybatisPlus+uniapp+uview2**等更多优秀组件及前沿技术开发，注释丰富，代码简洁，开箱即用。极其适合**项目分层实战**及业务流转对接，是个**练手学习简单项目的好机会**。\n<p align=\"center\">     \n    <p align=\"center\">\n       <a href=\'https://gitee.com/javadog-net/bkb-boot\'>\n       <img src=\'https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/72d673c08ad3408a855073c76fa408d0~tplv-k3u1fbpfcp-zoom-1.image\' alt=\'star\'></img>\n       </a>\n        <a href=\"https://www.uviewui.com/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/12b5532c30a741f2b4692e9c2cc3c494~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"uView\">\n        </a> \n        <a href=\"https://shiro.apache.org/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ef716f69d387435597a9f785a575516f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"shiro\">\n        </a> \n        <a href=\"http://spring.io/projects/spring-boot\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/78fce7b1c3ae4b74adec2008c807515c~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"spring-boot\">\n        </a>\n        <a href=\"http://mp.baomidou.com\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9eaff895d83c492a8871c4958faf60da~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"mybatis-plus\">\n        </a>  \n         <a href=\"http://hutool.cn/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/dd04bf1d1eea4de09a586c06c9449553~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"mybatis-plus\">\n        </a> \n        <a href=\"./LICENSE\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/12674878896f450eb1ec0062efc5f203~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"license Apache 2.0\">\n        </a>\n    </p>\n</p>\n\n******\n\n## 🎁快速链接\n### 公众号：**JavaDog程序狗**\n**关注公众号，发送 【bkb】或【避坑宝】，无任何套路即可获得**\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/80ec682f7bec493bafa8d4bd43624ffa~tplv-k3u1fbpfcp-zoom-1.image)\n******\n## 🌱体验地址\n### 小程序搜索【避坑宝】\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7573b59228a14daba9264b892ef2145b~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n##  🍯猜你喜欢\n### ChatGPT\n> 最近写了两篇关于**ChatGPT**相关文章，但因为审核原因未过审，跟大家抱歉\n\n[【ChatGPT】手摸手，带你玩转ChatGPT](https://mp.weixin.qq.com/s/9wEelbTN6kaChkCQHmgJMQ)\n[【ChatGPT】SpringBoot+uniapp+uview2对接OpenAI，带你开发玩转ChatGPT](https://mp.weixin.qq.com/s/b19J36Eo3-ba7bHbWzoZYQ)\n******\n\n## 🍵主要功能模块\n### 模块思维导图\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7ed61462fa6b4f629860ebd242f0e5ac~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### 🛳️授权\n##### 小程序授权\n 用户通过**小程序搜索**【避坑宝】，进入界面进行授权，点击授权登录，**获取用户openid**\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ef9b606cb56a46208890cc0e3f61e84f~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n#### 🍭首页\n##### 1. 企业红榜Top\n>展示综合评分前三的**优秀企业**，通过浏览量/点击量/评论量等综合得分\n\n##### 2. **企业黑榜榜Top**\n>展示综合评分前三的**低分企业**，通过浏览量/点击量/评论量等综合得分\n\n##### 3. **最新发布**\n>展示吐槽发布时间最新的三个企业\n\n##### 4.  **最新问题**\n> 展示问答回复最多的三个问题\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/eaa944e40639444ba398a49033bb1347~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### ⛄红黑榜\n##### 1.企业黑榜\n> 分页展示低评分分企业，综合评分越高排名越靠前\n\n##### 2.企业红榜\n> 分页展示优秀高评分企业，综合评分越高排名越靠前\n\n##### 3.企业详情\n> 展示吐槽企业信息，浏览/回复/点赞/热度等分数及发布时间，跟帖信息等\n\n![请添加图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/747b927e01ce4034b4b183f2d369f335~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### 🎃发布\n##### 1. 吐槽\n> 可对企业进行**吐槽**或者**赞扬**，对应发布的**红榜和黑榜**\n\n##### 2. 提问\n> 可针对企业或者工作问题进行**任何提问**\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/43b7156c5c2d4b478e712becbf3ec49b~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### 👻发布\n##### 1. 问题列表\n> 针对所有**提问**的一个**分页列表**，可进行搜索\n\n##### 2. 问题详情\n> 问题的详细描述，包含**点赞**和回**复数**以及**回复内容列表**\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9d5406b489ef47b7a3417ba3ff2015ee~tplv-k3u1fbpfcp-zoom-1.image)\n******\n#### 🌞我的\n##### 1. 我的资料\n> 可对自己**个人信息**进行维护，如昵称，性别，生日，简介\n\n##### 2. 我的吐槽\n> 本人发布过的吐槽信息，包含**红榜和黑榜**信息\n\n##### 3. 我的提问\n> 本人**发布过的提问信息**\n\n##### 4. 关于\n> webview嵌入的**本狗个人博客**\n\n##### 5. 退出\n> **授权信息清理**，跳转授权页面\n\n![请添加图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d52d531689814bfeaa9c75b81ee4f300~tplv-k3u1fbpfcp-zoom-1.image)\n![请添加图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4eb80e4b687242b6a89310818e8807c4~tplv-k3u1fbpfcp-zoom-1.image)\n\n## 🦁组件\n### 后端组件\n| 插件 | 版本 | 用途 |\n| --- | ----- |  ----- |\n| jdk |  1.8 |java环境 |\n| lombok | 1.18.16 |代码简化插件 |\n| maven | 3.6.3 |包管理工具 |\n| druid| 1.1.24 | JDBC组件 |\n| hutool| 5.7.20 | Java工具类库|\n| mybatis-plus| 3.4.1 | 基于 MyBatis 增强工具|\n| pinyin4j| 2.5.1 | 拼音组件库|\n| mysql | 8.0 / 5.7 | 数据库 |\n| weixin-java-miniapp| 4.4.5.B | 数据库 |\n\n### 前端组件\n| 插件 | 版本 | 用途 |\n| --- | ----- |  ----- |\n| uview-ui|  ^2.0.31 |多平台快速开发的UI框架 |\n| uni-simple-router | ^2.0.7 |专为uniapp打造的路由器 |\n| moment | ^2.29.4 |js工具库 |\n| mescroll-uni| ^1.3.7 | 上拉加载下拉刷新插件 |\n| @uni-ui/code-ui| ^1.4.8 | 二维码生成工具|\n\n******\n## 🛠️快速启动\n### 👸启动前端\n 1. 使用HBuilderX在bkb-uniapp项目目录下执行 \n	```\n	npm install\n	```\n 2. 记得将小程序对应的appid，写入manifest.json里面的对应位置\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/fc8e7e27afe14530ab985660daa83f92~tplv-k3u1fbpfcp-zoom-1.image)\n\n	![图片转存失败，建议将图片保存下来直接上传\n         在这里插入图片描述(https://img-blog.csdnimg.cn/5a57eb2518714668b1bd7bcf968d4dbf.png)\n\n]()	\n 3. 在HBuilderX工具下运行，选择需要运行到小程序\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/3ff44a9592004336a3ba2aa214689042~tplv-k3u1fbpfcp-zoom-1.image)\n\n 4. 如需调整环境，请在/common/config/env.js修改环境变量\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ca69a3ff5f5345eeb7234770b927656f~tplv-k3u1fbpfcp-zoom-1.image)\n******\n\n### 👦启动后端\n 1. 开发工具内配置好，并将**maven**配置好，建议使用**阿里云镜像**\n 2. **application.yml配置文件**中配置微信小程序参数**appId**，**appSecret**\n \n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/506cb037f08a4a25aed406b3eceddbb5~tplv-k3u1fbpfcp-zoom-1.image)\n\n 3. 将chat-provider包下的/doc/SQL/db_chat.sql进行本地执行，并在application配置文件中修改数据库配置\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4fc0b90d3c52490596bde972cde3de0e~tplv-k3u1fbpfcp-zoom-1.image)\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c583c99ffd2e4f2b98c17b1eefef5a88~tplv-k3u1fbpfcp-zoom-1.image)\n 	\n 4. 运行启动SpringBoot项目，如见到以下日志即为启动成功。\n\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/55e831ee6de348f8aaa57753cf9c7857~tplv-k3u1fbpfcp-zoom-1.image)\n******\n## 🤑体验\n![在这里插入图片描述](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/50b68f3b0905408985a73a0160e2809c~tplv-k3u1fbpfcp-zoom-1.image)\n## 😊总结\n本篇文章以**实战项目**形式进行展开，适合咱们初学者进行实际开发体验，熟练**SpringBoot**同时，也加深分层的思路，并且融会贯通前端**uniapp/vue**等知识，是一个**知识全面**的概括，内容简单通俗易懂，希望对大家有所帮助。\n\n![里面有我喜欢的人你能找到吗？](https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/dc0d63b469d141018ace1214790d27cd~tplv-k3u1fbpfcp-zoom-1.image)\n\n\n| JavaDog| 狗屋地址 |\n| :----:| :----: | \n| 个人博客 | [https://blog.javadog.net](https://blog.javadog.net) | \n| 公众号 | [https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw](https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw) | \n| CSDN  | [https://blog.csdn.net/baidu_25986059](https://blog.csdn.net/baidu_25986059) | \n| 掘金 | [https://juejin.cn/user/2172290706716775](https://juejin.cn/user/2172290706716775)| \n| 知乎 | [https://www.zhihu.com/people/JavaDog](https://www.zhihu.com/people/JavaDog) | \n| 简书| [https://www.jianshu.com/u/1ff9c6bdb916](https://www.jianshu.com/u/1ff9c6bdb916) | \n| gitee|[https://gitee.com/javadog-net](https://gitee.com/javadog-net)  | \n| GitHub|[https://github.com/javadog-net](https://github.com/javadog-net)| \n', '<hr />\n<h2><a id=\"theme_vuepress_1\"></a>theme: vuepress</h2>\n<div>\n    <p>\n        <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d8d26d62b5024e03a447ef5aae7bb328~tplv-k3u1fbpfcp-zoom-1.image\" height=\"150\" alt=\"logo\" />\n    </p>\n    <h1>避坑宝 v1.0.0</h1>\n	<h4>基于SpringBoot+uniapp企业黑红名单吐槽小程序</h4>\n</div>\n<h2><a id=\"_11\"></a>📚项目介绍</h2>\n<h3><a id=\"_12\"></a>避坑宝</h3>\n<p>【避坑宝】<strong>企业黑红名单吐槽小程序</strong>是一个具有<strong>吐槽发布企业信息的一个平台</strong>，言论自由，评判自定，便于我们打工人分辨企业好坏。技术栈基于<strong>SpringBoot+MybatisPlus+uniapp+uview2</strong>等更多优秀组件及前沿技术开发，注释丰富，代码简洁，开箱即用。极其适合<strong>项目分层实战</strong>及业务流转对接，是个<strong>练手学习简单项目的好机会</strong>。</p>\n<p>     \n    <p>\n       <a href=\"https://gitee.com/javadog-net/bkb-boot\">\n       <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/72d673c08ad3408a855073c76fa408d0~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"star\"></img>\n       </a>\n        <a href=\"https://www.uviewui.com/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/12b5532c30a741f2b4692e9c2cc3c494~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"uView\">\n        </a> \n        <a href=\"https://shiro.apache.org/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ef716f69d387435597a9f785a575516f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"shiro\">\n        </a> \n        <a href=\"http://spring.io/projects/spring-boot\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/78fce7b1c3ae4b74adec2008c807515c~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"spring-boot\">\n        </a>\n        <a href=\"http://mp.baomidou.com\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9eaff895d83c492a8871c4958faf60da~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"mybatis-plus\">\n        </a>  \n         <a href=\"http://hutool.cn/\">\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/dd04bf1d1eea4de09a586c06c9449553~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"mybatis-plus\">\n        </a> \n        <a href>\n            <img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/12674878896f450eb1ec0062efc5f203~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"license Apache 2.0\">\n        </a>\n    </p>\n</p>\n<hr />\n<h2><a id=\"_42\"></a>🎁快速链接</h2>\n<h3><a id=\"JavaDog_43\"></a>公众号：<strong>JavaDog程序狗</strong></h3>\n<p><strong>关注公众号，发送 【bkb】或【避坑宝】，无任何套路即可获得</strong></p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/80ec682f7bec493bafa8d4bd43624ffa~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h2><a id=\"_48\"></a>🌱体验地址</h2>\n<h3><a id=\"_49\"></a>小程序搜索【避坑宝】</h3>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7573b59228a14daba9264b892ef2145b~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h2><a id=\"_53\"></a>🍯猜你喜欢</h2>\n<h3><a id=\"ChatGPT_54\"></a>ChatGPT</h3>\n<blockquote>\n<p>最近写了两篇关于<strong>ChatGPT</strong>相关文章，但因为审核原因未过审，跟大家抱歉</p>\n</blockquote>\n<p><a href=\"https://mp.weixin.qq.com/s/9wEelbTN6kaChkCQHmgJMQ\" target=\"_blank\">【ChatGPT】手摸手，带你玩转ChatGPT</a><br />\n<a href=\"https://mp.weixin.qq.com/s/b19J36Eo3-ba7bHbWzoZYQ\" target=\"_blank\">【ChatGPT】SpringBoot+uniapp+uview2对接OpenAI，带你开发玩转ChatGPT</a></p>\n<hr />\n<h2><a id=\"_61\"></a>🍵主要功能模块</h2>\n<h3><a id=\"_62\"></a>模块思维导图</h3>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7ed61462fa6b4f629860ebd242f0e5ac~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"_66\"></a>🛳️授权</h4>\n<h5><a id=\"_67\"></a>小程序授权</h5>\n<p>用户通过<strong>小程序搜索</strong>【避坑宝】，进入界面进行授权，点击授权登录，<strong>获取用户openid</strong></p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ef9b606cb56a46208890cc0e3f61e84f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"_73\"></a>🍭首页</h4>\n<h5><a id=\"1_Top_74\"></a>1. 企业红榜Top</h5>\n<blockquote>\n<p>展示综合评分前三的<strong>优秀企业</strong>，通过浏览量/点击量/评论量等综合得分</p>\n</blockquote>\n<h5><a id=\"2_Top_77\"></a>2. <strong>企业黑榜榜Top</strong></h5>\n<blockquote>\n<p>展示综合评分前三的<strong>低分企业</strong>，通过浏览量/点击量/评论量等综合得分</p>\n</blockquote>\n<h5><a id=\"3__80\"></a>3. <strong>最新发布</strong></h5>\n<blockquote>\n<p>展示吐槽发布时间最新的三个企业</p>\n</blockquote>\n<h5><a id=\"4___83\"></a>4.  <strong>最新问题</strong></h5>\n<blockquote>\n<p>展示问答回复最多的三个问题</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/eaa944e40639444ba398a49033bb1347~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"_88\"></a>⛄红黑榜</h4>\n<h5><a id=\"1_89\"></a>1.企业黑榜</h5>\n<blockquote>\n<p>分页展示低评分分企业，综合评分越高排名越靠前</p>\n</blockquote>\n<h5><a id=\"2_92\"></a>2.企业红榜</h5>\n<blockquote>\n<p>分页展示优秀高评分企业，综合评分越高排名越靠前</p>\n</blockquote>\n<h5><a id=\"3_95\"></a>3.企业详情</h5>\n<blockquote>\n<p>展示吐槽企业信息，浏览/回复/点赞/热度等分数及发布时间，跟帖信息等</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/747b927e01ce4034b4b183f2d369f335~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"请添加图片描述\" /></p>\n<hr />\n<h4><a id=\"_100\"></a>🎃发布</h4>\n<h5><a id=\"1__101\"></a>1. 吐槽</h5>\n<blockquote>\n<p>可对企业进行<strong>吐槽</strong>或者<strong>赞扬</strong>，对应发布的<strong>红榜和黑榜</strong></p>\n</blockquote>\n<h5><a id=\"2__104\"></a>2. 提问</h5>\n<blockquote>\n<p>可针对企业或者工作问题进行<strong>任何提问</strong></p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/43b7156c5c2d4b478e712becbf3ec49b~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"_109\"></a>👻发布</h4>\n<h5><a id=\"1__110\"></a>1. 问题列表</h5>\n<blockquote>\n<p>针对所有<strong>提问</strong>的一个<strong>分页列表</strong>，可进行搜索</p>\n</blockquote>\n<h5><a id=\"2__113\"></a>2. 问题详情</h5>\n<blockquote>\n<p>问题的详细描述，包含<strong>点赞</strong>和回<strong>复数</strong>以及<strong>回复内容列表</strong></p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/9d5406b489ef47b7a3417ba3ff2015ee~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h4><a id=\"_118\"></a>🌞我的</h4>\n<h5><a id=\"1__119\"></a>1. 我的资料</h5>\n<blockquote>\n<p>可对自己<strong>个人信息</strong>进行维护，如昵称，性别，生日，简介</p>\n</blockquote>\n<h5><a id=\"2__122\"></a>2. 我的吐槽</h5>\n<blockquote>\n<p>本人发布过的吐槽信息，包含<strong>红榜和黑榜</strong>信息</p>\n</blockquote>\n<h5><a id=\"3__125\"></a>3. 我的提问</h5>\n<blockquote>\n<p>本人<strong>发布过的提问信息</strong></p>\n</blockquote>\n<h5><a id=\"4__128\"></a>4. 关于</h5>\n<blockquote>\n<p>webview嵌入的<strong>本狗个人博客</strong></p>\n</blockquote>\n<h5><a id=\"5__131\"></a>5. 退出</h5>\n<blockquote>\n<p><strong>授权信息清理</strong>，跳转授权页面</p>\n</blockquote>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d52d531689814bfeaa9c75b81ee4f300~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"请添加图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4eb80e4b687242b6a89310818e8807c4~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"请添加图片描述\" /></p>\n<h2><a id=\"_137\"></a>🦁组件</h2>\n<h3><a id=\"_138\"></a>后端组件</h3>\n<table>\n<thead>\n<tr>\n<th>插件</th>\n<th>版本</th>\n<th>用途</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>jdk</td>\n<td>1.8</td>\n<td>java环境</td>\n</tr>\n<tr>\n<td>lombok</td>\n<td>1.18.16</td>\n<td>代码简化插件</td>\n</tr>\n<tr>\n<td>maven</td>\n<td>3.6.3</td>\n<td>包管理工具</td>\n</tr>\n<tr>\n<td>druid</td>\n<td>1.1.24</td>\n<td>JDBC组件</td>\n</tr>\n<tr>\n<td>hutool</td>\n<td>5.7.20</td>\n<td>Java工具类库</td>\n</tr>\n<tr>\n<td>mybatis-plus</td>\n<td>3.4.1</td>\n<td>基于 MyBatis 增强工具</td>\n</tr>\n<tr>\n<td>pinyin4j</td>\n<td>2.5.1</td>\n<td>拼音组件库</td>\n</tr>\n<tr>\n<td>mysql</td>\n<td>8.0 / 5.7</td>\n<td>数据库</td>\n</tr>\n<tr>\n<td>weixin-java-miniapp</td>\n<td>4.4.5.B</td>\n<td>数据库</td>\n</tr>\n</tbody>\n</table>\n<h3><a id=\"_151\"></a>前端组件</h3>\n<table>\n<thead>\n<tr>\n<th>插件</th>\n<th>版本</th>\n<th>用途</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td>uview-ui</td>\n<td>^2.0.31</td>\n<td>多平台快速开发的UI框架</td>\n</tr>\n<tr>\n<td>uni-simple-router</td>\n<td>^2.0.7</td>\n<td>专为uniapp打造的路由器</td>\n</tr>\n<tr>\n<td>moment</td>\n<td>^2.29.4</td>\n<td>js工具库</td>\n</tr>\n<tr>\n<td>mescroll-uni</td>\n<td>^1.3.7</td>\n<td>上拉加载下拉刷新插件</td>\n</tr>\n<tr>\n<td>@uni-ui/code-ui</td>\n<td>^1.4.8</td>\n<td>二维码生成工具</td>\n</tr>\n</tbody>\n</table>\n<hr />\n<h2><a id=\"_161\"></a>🛠️快速启动</h2>\n<h3><a id=\"_162\"></a>👸启动前端</h3>\n<ol>\n<li>使用HBuilderX在bkb-uniapp项目目录下执行<pre><code class=\"lang-\">npm install\n</code></pre>\n</li>\n<li>记得将小程序对应的appid，写入manifest.json里面的对应位置</li>\n</ol>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/fc8e7e27afe14530ab985660daa83f92~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<pre><code>![图片转存失败，建议将图片保存下来直接上传\n     在这里插入图片描述(https://img-blog.csdnimg.cn/5a57eb2518714668b1bd7bcf968d4dbf.png)\n</code></pre>\n<p>]()	<br />\n3. 在HBuilderX工具下运行，选择需要运行到小程序<br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/3ff44a9592004336a3ba2aa214689042~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<ol start=\"4\">\n<li>如需调整环境，请在/common/config/env.js修改环境变量</li>\n</ol>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/ca69a3ff5f5345eeb7234770b927656f~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h3><a id=\"_183\"></a>👦启动后端</h3>\n<ol>\n<li>开发工具内配置好，并将<strong>maven</strong>配置好，建议使用<strong>阿里云镜像</strong></li>\n<li><strong>application.yml配置文件</strong>中配置微信小程序参数<strong>appId</strong>，<strong>appSecret</strong></li>\n</ol>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/506cb037f08a4a25aed406b3eceddbb5~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<ol start=\"3\">\n<li>将chat-provider包下的/doc/SQL/db_chat.sql进行本地执行，并在application配置文件中修改数据库配置</li>\n</ol>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/4fc0b90d3c52490596bde972cde3de0e~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /><br />\n<img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/c583c99ffd2e4f2b98c17b1eefef5a88~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<ol start=\"4\">\n<li>运行启动SpringBoot项目，如见到以下日志即为启动成功。</li>\n</ol>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/55e831ee6de348f8aaa57753cf9c7857~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<hr />\n<h2><a id=\"_198\"></a>🤑体验</h2>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/50b68f3b0905408985a73a0160e2809c~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"在这里插入图片描述\" /></p>\n<h2><a id=\"_200\"></a>😊总结</h2>\n<p>本篇文章以<strong>实战项目</strong>形式进行展开，适合咱们初学者进行实际开发体验，熟练<strong>SpringBoot</strong>同时，也加深分层的思路，并且融会贯通前端<strong>uniapp/vue</strong>等知识，是一个<strong>知识全面</strong>的概括，内容简单通俗易懂，希望对大家有所帮助。</p>\n<p><img src=\"https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/dc0d63b469d141018ace1214790d27cd~tplv-k3u1fbpfcp-zoom-1.image\" alt=\"里面有我喜欢的人你能找到吗？\" /></p>\n<table>\n<thead>\n<tr>\n<th style=\"text-align:center\">JavaDog</th>\n<th style=\"text-align:center\">狗屋地址</th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td style=\"text-align:center\">个人博客</td>\n<td style=\"text-align:center\"><a href=\"https://blog.javadog.net\" target=\"_blank\">https://blog.javadog.net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">公众号</td>\n<td style=\"text-align:center\"><a href=\"https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw\" target=\"_blank\">https://mp.weixin.qq.com/s/_vgnXoQ8FSobD3OfRAf5gw</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">CSDN</td>\n<td style=\"text-align:center\"><a href=\"https://blog.csdn.net/baidu_25986059\" target=\"_blank\">https://blog.csdn.net/baidu_25986059</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">掘金</td>\n<td style=\"text-align:center\"><a href=\"https://juejin.cn/user/2172290706716775\" target=\"_blank\">https://juejin.cn/user/2172290706716775</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">知乎</td>\n<td style=\"text-align:center\"><a href=\"https://www.zhihu.com/people/JavaDog\" target=\"_blank\">https://www.zhihu.com/people/JavaDog</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">简书</td>\n<td style=\"text-align:center\"><a href=\"https://www.jianshu.com/u/1ff9c6bdb916\" target=\"_blank\">https://www.jianshu.com/u/1ff9c6bdb916</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">gitee</td>\n<td style=\"text-align:center\"><a href=\"https://gitee.com/javadog-net\" target=\"_blank\">https://gitee.com/javadog-net</a></td>\n</tr>\n<tr>\n<td style=\"text-align:center\">GitHub</td>\n<td style=\"text-align:center\"><a href=\"https://github.com/javadog-net\" target=\"_blank\">https://github.com/javadog-net</a></td>\n</tr>\n</tbody>\n</table>\n', 0, 1, '2023-04-23 15:17:05', 1631537099054292993, '2023-04-23 15:17:05', NULL);

